command:example.dev:code:
PS C:\> $maxReturn = 10000
$containerName = "abc"

$total = 0
$token = $null
do
{
	$blobs = Get-AzureStorageBlob -Container $containerName -MaxCount $maxReturn  -ContinuationToken $token 
	$total += $blobs.Count
	if($blobs.Length -le 0) { break;}
	$token = $blobs[$blobs.Count -1].ContinuationToken;
} 
while($token -ne $null)
echo "Total $total blobs in container $containerName"

PS C:\> $maxReturn = 10000


$total = 0
$token = $null
do
{
	$containers = Get-AzureStorageContainer -MaxCount $maxReturn  -ContinuationToken $token
	$total += $containers.Count
	if($containers.Length -le 0) { break;}
	$token = $containers[$containers.Count-1].ContinuationToken;
} 
while($token -ne $null)

echo "Total $total containers"

PS C:\> $CorsRules = (@{
    AllowedHeaders=@("x-ms-blob-content-type","x-ms-blob-content-disposition");
    AllowedOrigins=@("*");
    MaxAgeInSeconds=30;
    AllowedMethods=@("Get","Connect")},
    @{
    AllowedOrigins=@("http://www.fabrikam.com","http://www.contoso.com"); 
    ExposedHeaders=@("x-ms-meta-data*","x-ms-meta-customheader"); 
    AllowedHeaders=@("x-ms-meta-target*","x-ms-meta-customheader");
    MaxAgeInSeconds=30;
    AllowedMethods=@("Put")})
PS C:\> Set-AzureStorageCORSRule -ServiceType Blob -CorsRules $CorsRules
