if(($null -eq $TestName) -or ($TestName -contains 'New-AzConfidentialLedgerCertBasedSecurityPrincipalObject'))
{
  $loadEnvPath = Join-Path $PSScriptRoot 'loadEnv.ps1'
  if (-Not (Test-Path -Path $loadEnvPath)) {
      $loadEnvPath = Join-Path $PSScriptRoot '..\loadEnv.ps1'
  }
  . ($loadEnvPath)
  $TestRecordingFile = Join-Path $PSScriptRoot 'New-AzConfidentialLedgerCertBasedSecurityPrincipalObject.Recording.json'
  $currentPath = $PSScriptRoot
  while(-not $mockingPath) {
      $mockingPath = Get-ChildItem -Path $currentPath -Recurse -Include 'HttpPipelineMocking.ps1' -File
      $currentPath = Split-Path -Path $currentPath -Parent
  }
  . ($mockingPath | Select-Object -First 1).FullName
}

Describe 'New-AzConfidentialLedgerCertBasedSecurityPrincipalObject' {
    It '__AllParameterSets' {
        $certSecurityPrincipal = New-AzConfidentialLedgerCertBasedSecurityPrincipalObject `
            -Cert "-----BEGIN CERTIFICATE----------END CERTIFICATE-----" `
            -LedgerRoleName "Reader"

        $certSecurityPrincipal.Cert | Should -Be "-----BEGIN CERTIFICATE----------END CERTIFICATE-----"
        $certSecurityPrincipal.LedgerRoleName | Should -Be "Reader"
    }
}
