{
  "properties": {
    "datasourceTypes": [ "Microsoft.DBforPostgreSQL/servers/databases" ],
    "objectType": "BackupPolicy",
    "policyRules": [
      {
        "name": "BackupWeekly",
        "objectType": "AzureBackupRule",
        "backupParameters": {
          "objectType": "AzureBackupParams",
          "backupType": "Full"
        },
        "dataStore": {
          "dataStoreType": "VaultStore",
          "objectType": "DataStoreInfoBase"
        },
        "trigger": {
          "objectType": "ScheduleBasedTriggerContext",
          "schedule": {
            "repeatingTimeIntervals": [ "R/2021-08-18T10:00:00+05:30/P1W", "R/2021-08-22T10:00:00+05:30/P1W" ],
            "timeZone": "India Standard Time"
          },
          "taggingCriteria": [
            {
              "tagInfo": {
                "tagName": "Default"
              },
              "isDefault": true,
              "taggingPriority": 99
            },
            {
              "tagInfo": {
                "tagName": "Monthly"
              },
              "criteria": [
                {
                  "objectType": "ScheduleBasedBackupCriteria",
                  "absoluteCriteria": [ "FirstOfMonth" ]
                }
              ],
              "isDefault": false,
              "taggingPriority": 15
            }
          ]
        }
      },
      {
        "name": "Default",
        "objectType": "AzureRetentionRule",
        "isDefault": true,
        "lifecycles": [
          {
            "deleteAfter": {
              "duration": "P3M",
              "objectType": "AbsoluteDeleteOption"
            },
            "sourceDataStore": {
              "dataStoreType": "VaultStore",
              "objectType": "DataStoreInfoBase"
            },
            "targetDataStoreCopySettings": [
              {
                "copyAfter": {
                  "objectType": "CopyOnExpiryOption"
                },
                "dataStore": {
                  "dataStoreType": "ArchiveStore",
                  "objectType": "DataStoreInfoBase"
                }
              }
            ]
          },
          {
            "deleteAfter": {
              "duration": "P6M",
              "objectType": "AbsoluteDeleteOption"
            },
            "sourceDataStore": {
              "dataStoreType": "ArchiveStore",
              "objectType": "DataStoreInfoBase"
            }
          }
        ]
      },
      {
        "name": "Monthly",
        "objectType": "AzureRetentionRule",
        "isDefault": false,
        "lifecycles": [
          {
            "deleteAfter": {
              "duration": "P6M",
              "objectType": "AbsoluteDeleteOption"
            },
            "sourceDataStore": {
              "dataStoreType": "VaultStore",
              "objectType": "DataStoreInfoBase"
            },
            "targetDataStoreCopySettings": [
              {
                "copyAfter": {
                  "objectType": "CopyOnExpiryOption"
                },
                "dataStore": {
                  "dataStoreType": "ArchiveStore",
                  "objectType": "DataStoreInfoBase"
                }
              }
            ]
          },
          {
            "deleteAfter": {
              "duration": "P12M",
              "objectType": "AbsoluteDeleteOption"
            },
            "sourceDataStore": {
              "dataStoreType": "ArchiveStore",
              "objectType": "DataStoreInfoBase"
            }
          }
        ]
      }
    ]
  }
}

