{
  "properties": {
    "displayName": "\"[AUDIT TEST]: Deploy VM extension to audit that an application is installed inside Linux VMs\"",
    "policyType": "Custom",
    "mode": "All",
    "description": "Include this rule to deploy the VM extension for Microsoft Guest Configuration, the VM extension for Microsoft Azure Managed Service Identity, and the content required to audit that an application is installed inside Linux virtual machines. This policy should only be used along with its corresponding audit policy in an initiative/policy set. For more information on guest configuration policies, please visit http://aka.ms/gcpol",
    "metadata": {
      "category": "Guest Configuration",
      "requiredProviders": [
        "Microsoft.GuestConfiguration"
      ]
    },
    "parameters": {
      "ApplicationName": {
        "type": "String",
        "metadata": {
          "displayName": "Application name",
          "description": "Application name. Example: 'python' or 'powershell'."
        }
      }
    },
    "policyRule": {
      "if": {
        "allOf": [
          {
            "field": "type",
            "equals": "Microsoft.Compute/virtualMachines"
          },
          {
            "field": "Microsoft.Compute/virtualMachines/osProfile.windowsConfiguration",
            "exists": "true"
          },
          {
            "not": {
              "anyOf": [
                {
                  "allOf": [
                    {
                      "field": "Microsoft.Compute/imagePublisher",
                      "equals": "MicrosoftWindowsServer"
                    },
                    {
                      "field": "Microsoft.Compute/imageOffer",
                      "equals": "WindowsServer"
                    },
                    {
                      "field": "Microsoft.Compute/imageSKU",
                      "in": [
                        "2008-R2-SP1"
                      ]
                    }
                  ]
                }
              ]
            }
          }
        ]
      },
      "then": {
        "effect": "deployIfNotExists",
        "details": {
          "type": "Microsoft.GuestConfiguration/guestConfigurationAssignments",
          "name": "installed_application_linux",
