            "Order_Failure": {
              "actions": {
                "Can_we_call_back_with_order_failure": {
                  "actions": {
                    "Call_Back_with_Order_Failure": {
                      "runAfter": {},
                      "type": "Http",
                      "inputs": {
                        "body": {
                          "landmarkOrderID": "@{variables('orderId')}",
                          "orderStatus": "Errored",
                          "orderStatusDescription": "@{coalesce(variables('ScopeException')?['cpackLineMessage'],variables('ExceptionMessage'))}"
                        },
                        "headers": "@variables('CallBackHeaders')",
                        "method": "POST",
                        "retryPolicy": {
                          "count": 90,
                          "interval": "PT5M",
                          "type": "fixed"
                        },
                        "uri": "@body('ParseOrderDetails')?['callBackURL']"
                      }
                    }
                  },
                  "runAfter": {
                    "Do_we_have_a_product_line_to_update": [
                      "Succeeded"
                    ]
                  },
                  "expression": {
                    "and": [
                      {
                        "not": {
                          "equals": [
                            "@coalesce(body('ParseOrderDetails')?['callBackURL'],'undefined')",
                            "undefined"
                          ]
                        }
                      }
                    ]
                  },
                  "type": "If"
                },
                "CleanCPackMessage": {
                  "runAfter": {
                    "Heroku_Update_Order_Status": [
                      "Succeeded"
                    ]
                  },
                  "type": "Function",
                  "inputs": {
                    "body": {
                      "max": 255,
                      "value": "@{coalesce(variables('ScopeException')?['cpackLineMessage'],variables('ExceptionMessage'))}"
                    },
                    "function": {
                      "id": "/subscriptions/f34b22a3-2202-4fb1-b040-1332bd928c84/resourceGroups/jonsigniterg/providers/Microsoft.Web/sites/CustomerFunctions/functions/AddDateTIme"
                    }
                  }
                },
                "CleanOrderLineMessage": {
                  "runAfter": {
                    "Update_Product_Errored": [
                      "Succeeded"
                    ]
                  },
                  "type": "Function",
                  "inputs": {
                    "body": {
                      "max": 255,
                      "value": "@{coalesce(variables('ScopeException')?['orderMessage'],variables('ExceptionMessage'))}"
                    },
                    "function": {
                      "id": "/subscriptions/f34b22a3-2202-4fb1-b040-1332bd928c84/resourceGroups/jonsigniterg/providers/Microsoft.Web/sites/CustomerFunctions/functions/AddDateTIme"
                    }
                  }
                },
                "Compose_Product_Status_to_Errored": {
                  "runAfter": {},
                  "type": "Compose",
                  "inputs": "@setProperty(  setProperty(variables('TheProduct'),'status','Errored') ,'orderStatusDescription',coalesce(variables('ScopeException')?['orderMessage'],variables('ExceptionMessage')))"
                },
                "Do_we_have_a_product_line_to_update": {
                  "actions": {
                    "CleanItemMessage": {
                      "runAfter": {},
                      "type": "Function",
                      "inputs": {
                        "body": {
                          "max": 255,
                          "value": "@{variables('ScopeException')?['itemMessage']}"
                        },
                        "function": {
                          "id": "/subscriptions/f34b22a3-2202-4fb1-b040-1332bd928c84/resourceGroups/jonsigniterg/providers/Microsoft.Web/sites/CustomerFunctions/functions/AddDateTIme"
                        }
                      }
                    },
                    "Heroku_Update_Order_Lines": {
                      "runAfter": {
                        "CleanItemMessage": [
                          "Succeeded"
                        ]
                      },
                      "type": "Http",
                      "inputs": {
                        "body": {
                          "statusDescription": "@{body('CleanItemMessage')?['result']}"
                        },
                        "headers": {
                          "Content-Type": "application/json",
                          "x-api-key": "@{parameters('HEROKU_CRUD_SUBSCRIPTION_KEY')}"
                        },
                        "method": "PUT",
                        "uri": "@{concat(parameters('HEROKU_CRUD_URI'),'orderItem/',variables('ScopeException')?['orderItemId'],'/status/errored')}"
                      }
                    }
                  },
                  "runAfter": {
                    "Heroku_Update_Compliance_Pack_Order_Line": [
                      "Succeeded"
                    ]
                  },
                  "expression": {
                    "and": [
                      {
                        "not": {
                          "equals": [
                            "@coalesce(variables('ScopeException')?['orderItemId'],'undefined')",
                            "undefined"
                          ]
                        }
                      }
                    ]
                  },
                  "type": "If"
                },
                "Heroku_Update_Compliance_Pack_Order_Line": {
                  "runAfter": {
                    "CleanCPackMessage": [
                      "Succeeded"
                    ]
                  },
                  "type": "Http",
                  "inputs": {
                    "body": {
                      "statusDescription": "@{body('CleanCpackMessage')?['result']}"
                    },
                    "headers": {
                      "Content-Type": "application/json",
                      "x-api-key": "@{parameters('HEROKU_CRUD_SUBSCRIPTION_KEY')}"
                    },
                    "method": "PUT",
                    "uri": "@{concat(parameters('HEROKU_CRUD_URI'),'orderItem/',variables('cpackOrderItemId'),'/status/errored')}"
                  }
                },
                "Heroku_Update_Order_Status": {
                  "runAfter": {
                    "CleanOrderLineMessage": [
                      "Succeeded"
                    ]
                  },
                  "type": "Http",
                  "inputs": {
                    "body": {
                      "status": "Errored",
                      "statusDescription": "@{body('CleanOrderLineMessage')?['result']}"
                    },
                    "headers": {
                      "Content-Type": "application/json",
                      "x-api-key": "@{parameters('HEROKU_CRUD_SUBSCRIPTION_KEY')}"
                    },
                    "method": "PUT",
                    "uri": "@{concat(parameters('HEROKU_CRUD_URI'),'order/',variables('orderId'))}"
                  }
