"data": "<Base64-encoded-file>",
"dataType": "<file-format>",
"password": "<pfx-file-password>"
}

command:example.dev:code:
$rgName = 'MyResourceGroup';
$vmName = 'MyWindowsVM';

$aadClientID = "<clientID of your AAD app>";
$aadClientSecret = "<clientSecret of your AAD app>";

$vaultName= 'MyKeyVault';
$keyVault = Get-AzureRmKeyVault -VaultName $vaultName -ResourceGroupName $rgname;
$diskEncryptionKeyVaultUrl = $keyVault.VaultUri;
$keyVaultResourceId = $keyVault.ResourceId;

Set-AzureRmVMDiskEncryptionExtension -ResourceGroupName $rgname -VMName $vmName -AadClientID $aadClientID -AadClientSecret $aadClientSecret -DiskEncryptionKeyVaultUrl $diskEncryptionKeyVaultUrl -DiskEncryptionKeyVaultId $keyVaultResourceId ;

$rgName = 'MyResourceGroup';
$vmName = 'MyWindowsVM';

#The KeyVault must have enabledForDiskEncryption property set on it
$vaultName= 'MyKeyVault';
$keyVault = Get-AzureRmKeyVault -VaultName $vaultName -ResourceGroupName $rgname;
$diskEncryptionKeyVaultUrl = $keyVault.VaultUri;
$keyVaultResourceId = $keyVault.ResourceId;

# create AAD application and associate the certificate
$certPath = 'C:\certificates\examplecert.pfx';
$certPassword = 'yourPassword';
$cert = New-Object System.Security.Cryptography.X509Certificates.X509Certificate2($certPath, $certPassword);
$keyValue = [System.Convert]::ToBase64String($cert.GetRawCertData());
$azureAdApplication = New-AzureRmADApplication -DisplayName "<Your Application Display Name>" -HomePage "<https://YourApplicationHomePage>" -IdentifierUris "<https://YouApplicationUri>" -KeyValue $keyValue -KeyType AsymmetricX509Cert ;
$servicePrincipal = New-AzureRmADServicePrincipal –ApplicationId $azureAdApplication.ApplicationId;

$aadClientID = $azureAdApplication.ApplicationId;
$aadClientCertThumbprint= $cert.Thumbprint;

#Upload pfx to KeyVault 
$keyVaultSecretName = 'myaadcert';
$fileContentBytes = get-content $certPath -Encoding Byte;
$fileContentEncoded = [System.Convert]::ToBase64String($fileContentBytes);
$jsonObject = @" { "data": "$filecontentencoded", "dataType" :"pfx", "password": "$certPassword" } "@ ;
$jsonObjectBytes = [System.Text.Encoding]::UTF8.GetBytes($jsonObject);
$jsonEncoded = [System.Convert]::ToBase64String($jsonObjectBytes);

$secret = ConvertTo-SecureString -String $jsonEncoded -AsPlainText -Force;
Set-AzureKeyVaultSecret -VaultName $vaultName-Name $keyVaultSecretName -SecretValue $secret;
Set-AzureRmKeyVaultAccessPolicy -VaultName $vaultName -ResourceGroupName $rgName –EnabledForDeployment;

#deploy cert to VM
$certUrl = (Get-AzureKeyVaultSecret -VaultName $vaultName -Name $keyVaultSecretName).Id
$sourceVaultId = (Get-AzureRmKeyVault -VaultName $vaultName -ResourceGroupName $rgName).ResourceId
$vm = Get-AzureRmVM -ResourceGroupName $rgName -Name $vmName 
$vm = Add-AzureRmVMSecret -VM $vm -SourceVaultId $sourceVaultId -CertificateStore "My" -CertificateUrl $certUrl 
Update-AzureRmVM -VM $vm  -ResourceGroupName $rgName 

#Enable encryption on the VM using AAD client ID and client cert thumbprint
Set-AzureRmVMDiskEncryptionExtension -ResourceGroupName $rgname -VMName $vmName -AadClientID $aadClientID -AadClientCertThumbprint $aadClientCertThumbprint -DiskEncryptionKeyVaultUrl $diskEncryptionKeyVaultUrl -DiskEncryptionKeyVaultId $keyVaultResourceId ;

$rgName = 'MyResourceGroup';
$vmName = 'MyWindowsVM';

$aadClientID = "<clientID of your AAD app>";
$aadClientSecret = "<clientSecret of your AAD app>";

$vaultName= 'MyKeyVault';
$keyVault = Get-AzureRmKeyVault -VaultName $vaultName -ResourceGroupName $rgname;
$diskEncryptionKeyVaultUrl = $keyVault.VaultUri;
$keyVaultResourceId = $keyVault.ResourceId;

$kek = Add-AzureKeyVaultKey -VaultName $vaultName -Name $kekName -Destination "Software"
$keyEncryptionKeyUrl = $kek.Key.kid;

Set-AzureRmVMDiskEncryptionExtension -ResourceGroupName $rgname -VMName $vmName -AadClientID $aadClientID -AadClientSecret $aadClientSecret -DiskEncryptionKeyVaultUrl $diskEncryptionKeyVaultUrl -DiskEncryptionKeyVaultId $keyVaultResourceId -KeyEncryptionKeyUrl $keyEncryptionKeyUrl -KeyEncryptionKeyVaultId $keyVaultResourceId;

