command:example.dev:code:
# First create the policy
$p = New-AzureRmIntuneAndroidMAMPolicy -FriendlyName AndroidTestPolicy
# Get the list of apps
$apps = Get-AzureRmIntuneAndroidMAMApp;

# Finally, link the app to the Android Policy
Add-AzureRmIntuneAndroidMAMPolicyApp -Name $p.name -AppName $apps[0].Name;

# First create the policy $p = New-AzureRmIntuneAndroidMAMPolicy -FriendlyName AndroidTestPolicy
# Get the list of AAD groups $groups = @(Get-AzureRmADGroup);
# Now add the Group to the Android policy. Add-AzureRmIntuneAndroidMAMPolicyGroup -Name $p.Name -GroupName $groups[0].Id

# First create the policy
$p = New-AzureRmIntuneiOSMAMPolicy -FriendlyName iOSTestPolicy
# Get the list of apps
$apps = Get-AzureRmIntuneiOSMAMApp;

# Finally, link the app to the iOS Policy
Add-AzureRmIntuneiOSMAMPolicyApp -Name $p.name -AppName $apps[0].Name;

# First create the policy $p = New-AzureRmIntuneiOSMAMPolicy -FriendlyName iOSTestPolicy
# Get the list of AAD groups $groups = @(Get-AzureRmADGroup);
# Now add the Group to the iOS policy. Add-AzureRmIntuneiOSMAMPolicyGroup -Name $p.Name -GroupName $groups[0].Id

# First create the policy
$p = New-AzureRmIntuneAndroidMAMPolicy -FriendlyName AndroidTestPolicy
# Get the list of apps
$apps = Get-AzureRmIntuneAndroidMAMApp;

# link the app to the Android Policy
Add-AzureRmIntuneAndroidMAMPolicyApp -Name $p.name -AppName $apps[0].Name;

# Get the linked apps
$apps = Get-AzureRmIntuneAndroidMAMPolicyApp -Name $p.Name
$apps;

# First create the policy
$p = New-AzureRmIntuneiOSMAMPolicy -FriendlyName iOSTestPolicy

# Get the list of apps
$apps = Get-AzureRmIntuneiOSMAMApp;

# link the app to the iOS Policy
Add-AzureRmIntuneiOSMAMPolicyApp -Name $p.name -AppName $apps[0].Name;

# Get the linked apps
$apps = Get-AzureRmIntuneiOSMAMPolicyApp -Name $p.Name
$apps;

# Get the list of AAD users $users = @(Get-AzureRmADUser);
# Get the devices for the user $devices = Get-AzureRmIntuneMAMUserDevice -Name $users[0].Id
# Get a single device for the user Get-AzureRmIntuneMAMUserDevice -Name $users[0].Id -DeviceName $devices[0].Name

# Get the list of AAD users
$users = @(Get-AzureRmADUser);

Get-AzureRmIntuneMAMUserFlaggedEnrolledApp -Name $users[0].Id

# First create the policy $p = New-AzureRmIntuneAndroidMAMPolicy -FriendlyName AndroidTestPolicy
# Get the list of AAD groups $groups = @(Get-AzureRmADGroup);
# Add the Group to the Android policy. Add-AzureRmIntuneAndroidMAMPolicyGroup -Name $p.Name -GroupName $groups[0].Id
# Now remove the link. Remove-AzureRmIntuneAndroidMAMPolicyGroup -Name $p.name -GroupName $groups[0].Name -Force;

# First create the policy
$p = New-AzureRmIntuneiOSMAMPolicy -FriendlyName iOSTestPolicy

# Get the list of apps
$apps = Get-AzureRmIntuneiOSMAMApp;

# Link the app to the iOS Policy
Add-AzureRmIntuneiOSMAMPolicyApp -Name $p.name -AppName $apps[0].Name;

# Now remove the link.
Remove-AzureRmIntuneiOSMAMPolicyApp -Name $p.name -AppName $apps[0].Name -Force;

# First create the policy $p = New-AzureRmIntuneiOSMAMPolicy -FriendlyName iOSTestPolicy
# Get the list of AAD groups $groups = @(Get-AzureRmADGroup);
# Add the Group to the iOS policy. Add-AzureRmIntuneiOSMAMPolicyGroup -Name $p.Name -GroupName $groups[0].Id
# Now remove the link. Remove-AzureRmIntuneiOSMAMPolicyGroup -Name $p.name -GroupName $groups[0].Name -Force;

# First create the policy $p = New-AzureRmIntuneAndroidMAMPolicy -FriendlyName AndroidTestPolicy
# Now patch it. $p = Set-AzureRmIntuneAndroidMAMPolicy -Name $p.Name -AppSharingToLevel allApps -FriendlyName "New Android Policy Patched" -Force
# Display it $p;

# First create the policy $p = New-AzureRmIntuneiOSMAMPolicy -FriendlyName iOSTestPolicy
# Now patch it. $p = Set-AzureRmIntuneiOSMAMPolicy -Name $p.Name -AppSharingToLevel allApps -FriendlyName "New iOS Policy Patched" -Force
# Display it $p;
