{
  "properties": {
    "policyType": "Custom",
    "mode": "Microsoft.Kubernetes.Data",
    "displayName": "Kubernetes cluster containers should only use allowed images",
    "description": "Cloned from built-in DP policy: febd0533-8e55-448f-b837-bd0e06f16469",
    "policyRule": {
      "if": {
        "field": "type",
        "in": [ "Microsoft.Kubernetes/connectedClusters", "Microsoft.ContainerService/managedClusters" ]
      },
      "then": {
        "effect": "[parameters(\u0027effect\u0027)]",
        "details": {
          "warn": "[parameters(\u0027warn\u0027)]",
          "templateInfo": {
            "sourceType": "PublicURL",
            "url": "https://store.policy.core.windows.net/kubernetes/container-allowed-images/v2/template.yaml"
          },
          "apiGroups": [ "" ],
          "kinds": [ "Pod" ],
          "excludedNamespaces": "[parameters(\u0027excludedNamespaces\u0027)]",
          "namespaces": "[parameters(\u0027namespaces\u0027)]",
          "labelSelector": "[parameters(\u0027labelSelector\u0027)]",
          "values": {
            "imageRegex": "[parameters(\u0027allowedContainerImagesRegex\u0027)]",
            "excludedContainers": "[parameters(\u0027excludedContainers\u0027)]"
          }
        }
      }
    },
    "metadata": {
      "version": "9.2.0",
      "category": "Kubernetes"
    },
    "parameters": {
      "warn": {
        "type": "Boolean",
        "metadata": {
          "displayName": "Warn",
          "description": "Whether or not to return warnings back to the user in the kubectl cli"
        },
        "allowedValues": [ true, false ],
        "defaultValue": false
      },
      "effect": {
        "type": "String",
        "metadata": {
          "displayName": "Effect",
          "description": "\u0027Audit\u0027 allows a non-compliant resource to be created, but flags it as non-compliant. \u0027Deny\u0027 blocks the resource creation. \u0027Disable\u0027 turns off the policy.",
          "portalReview": true
        },
        "allowedValues": [ "audit", "Audit", "deny", "Deny", "disabled", "Disabled" ],
        "defaultValue": "Deny"
      },
      "excludedNamespaces": {
        "type": "Array",
        "metadata": {
          "displayName": "Namespace exclusions",
          "description": "List of Kubernetes namespaces to exclude from policy evaluation. System namespaces \"kube-system\", \"gatekeeper-system\" and \"azure-arc\" are always excluded by design. \"azure-extensions-usage-system\" is optional to remove."
        },
        "defaultValue": [ "kube-system", "gatekeeper-system", "azure-arc", "azure-extensions-usage-system" ]
      },
      "namespaces": {
        "type": "Array",
        "metadata": {
          "displayName": "Namespace inclusions",
          "description": "List of Kubernetes namespaces to only include in policy evaluation. An empty list means the policy is applied to all resources in all namespaces."
        },
        "defaultValue": [ ]
      },
      "labelSelector": {
        "type": "Object",
        "metadata": {
          "displayName": "Kubernetes label selector",
          "description": "Label query to select Kubernetes resources for policy evaluation. An empty label selector matches all Kubernetes resources."
        },
        "defaultValue": {
        },
        "schema": {
          "description": "A label selector is a label query over a set of resources. The result of matchLabels and matchExpressions are ANDed. An empty label selector matches all resources.",
          "type": "object",
          "properties": {
            "matchLabels": {
              "description": "matchLabels is a map of {key,value} pairs.",
              "type": "object",
              "additionalProperties": {
                "type": "string"
              },
              "minProperties": 1
            },
            "matchExpressions": {
              "description": "matchExpressions is a list of values, a key, and an operator.",
              "type": "array",
              "items": {
                "type": "object",
                "properties": {
                  "key": {
                    "description": "key is the label key that the selector applies to.",
                    "type": "string"
                  },
                  "operator": {
                    "description": "operator represents a key\u0027s relationship to a set of values.",
                    "type": "string",
                    "enum": [ "In", "NotIn", "Exists", "DoesNotExist" ]
                  },
                  "values": {
                    "description": "values is an array of string values. If the operator is In or NotIn, the values array must be non-empty. If the operator is Exists or DoesNotExist, the values array must be empty.",
                    "type": "array",
                    "items": {
                      "type": "string"
                    }
                  }
                },
                "required": [ "key", "operator" ],
                "additionalProperties": false
              },
              "minItems": 1
            }
          },
          "additionalProperties": false
        }
      },
      "allowedContainerImagesRegex": {
        "type": "String",
        "metadata": {
          "displayName": "Allowed registry or registries regex",
          "description": "The RegEx rule used to match allowed container image field in a Kubernetes cluster. For example, to allow any Azure Container Registry image by matching partial path: ^[^\\/]+\\.azurecr\\.io\\/.+$ and for multiple registries: ^([^\\/]+\\.azurecr\\.io|registry\\.io)\\/.+$",
          "portalReview": true
        }
      },
      "excludedContainers": {
        "type": "Array",
        "metadata": {
          "displayName": "Containers exclusions",
          "description": "The list of InitContainers and Containers to exclude from policy evaluation. The identify is the name of container. Use an empty list to apply this policy to all containers in all namespaces."
        },
        "defaultValue": [ ]
      }
    }
  }
}

