{
  "properties": {
    "mode": "All",
    "policyRule": {
      "if": {
        "allOf": [
          {
            "field": "type",
            "equals": "Microsoft.Compute/virtualMachines"
          },
          {
            "anyof": [
              {
                "field": "Microsoft.Compute/virtualMachines/diagnosticsProfile.bootDiagnostics.storageUri",
                "exists": true
              },
              {
                "field": "Microsoft.Compute/virtualMachines/diagnosticsProfile.bootDiagnostics.enabled",
                "notEquals": true
              }
            ]
          }
        ]
      },
      "then": {
        "effect": "[parameters(\u0027effect\u0027)]",
        "details": {
          "conflictEffect": "audit",
          "roleDefinitionIds": [ "/providers/Microsoft.Authorization/roleDefinitions/9980e02c-c2be-4d73-94e8-173b1dc7cf3c" ],
          "operations": [
            {
              "operation": "addOrReplace",
              "field": "Microsoft.Compute/virtualMachines/diagnosticsProfile.bootDiagnostics.storageUri",
              "value": null
            },
            {
              "operation": "addOrReplace",
              "field": "Microsoft.Compute/virtualMachines/diagnosticsProfile.bootDiagnostics.enabled",
              "value": true
            }
          ]
        }
      }
    },
    "parameters": {
      "effect": {
        "type": "String",
        "metadata": {
          "displayName": "Effect",
          "description": "\u0027Audit\u0027 allows a non-compliant resource to be created, but flags it as non-compliant. \u0027Deny\u0027 blocks the resource creation. \u0027Disable\u0027 turns off the policy.",
          "portalReview": true
        },
        "allowedValues": [ "audit", "Audit", "deny", "Deny", "disabled", "Disabled" ],
        "defaultValue": "Deny"
      }
    }
  }
}
