                "in": [
                  "18.04-LTS",
                  "16.04-LTS",
                  "16.04.0-LTS",
                  "14.04.2-LTS",
                  "12.04.5-LTS"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
