      "id": "/providers/Microsoft.Authorization/policyDefinitions/49c88fc8-6fd1-46fd-a676-f12d1d3a4c71",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "49c88fc8-6fd1-46fd-a676-f12d1d3a4c71"
    },
    {
      "properties": {
        "displayName": "Deploy requirements to audit Linux VMs that do not have the specified applications installed",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy creates a Guest Configuration assignment to audit Linux virtual machines that do not have the specified applications installed. It also creates a system-assigned managed identity and deploys the VM extension for Guest Configuration. This policy should only be used along with its corresponding audit policy in an initiative. For more information on Guest Configuration policies, please visit https://aka.ms/gcpol",
        "metadata": {
          "category": "Guest Configuration",
          "requiredProviders": [
            "Microsoft.GuestConfiguration"
          ]
        },
        "parameters": {
          "ApplicationName": {
            "type": "String",
            "metadata": {
              "displayName": "Application names",
              "description": "A semicolon-separated list of the names of the applications that should be installed. e.g. 'python; powershell'"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/imagePublisher",
                    "in": [
                      "microsoft-aks",
                      "AzureDatabricks",
                      "qubole-inc",
                      "datastax",
                      "couchbase",
                      "scalegrid",
                      "checkpoint",
                      "paloaltonetworks"
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "OpenLogic"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "like": "CentOS*"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "notLike": "6*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "RHEL"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "notLike": "6*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "osa"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "credativ"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "Debian"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "notLike": "7*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Suse"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "like": "SLES*"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "notLike": "11*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "UbuntuServer"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "notLike": "12*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "microsoft-dsvm"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "linux-data-science-vm-ubuntu",
                          "azureml"
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "cloudera"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "cloudera-centos-os"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "notLike": "6*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "cloudera"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "cloudera-altus-centos-os"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "microsoft-ads"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "like": "linux*"
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.GuestConfiguration/guestConfigurationAssignments",
              "name": "installed_application_linux",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
              ],
              "existenceCondition": {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/parameterHash",
                "equals": "[base64(concat('[ChefInSpec]InstalledApplicationLinuxResource1;AttributesYmlContent', '=', concat('packages: [', replace(parameters('ApplicationName'), ';', ','), ']')))]"
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "configurationName": {
                      "value": "installed_application_linux"
                    },
                    "ApplicationName": {
                      "value": "[parameters('ApplicationName')]"
                    }
                  },
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "configurationName": {
                        "type": "string"
                      },
                      "ApplicationName": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "apiVersion": "2018-11-20",
                        "type": "Microsoft.Compute/virtualMachines/providers/guestConfigurationAssignments",
                        "name": "[concat(parameters('vmName'), '/Microsoft.GuestConfiguration/', parameters('configurationName'))]",
                        "location": "[parameters('location')]",
                        "properties": {
                          "guestConfiguration": {
                            "name": "[parameters('configurationName')]",
                            "version": "1.*",
                            "configurationParameter": [
                              {
                                "name": "[ChefInSpec]InstalledApplicationLinuxResource1;AttributesYmlContent",
                                "value": "[concat('packages: [', replace(parameters('ApplicationName'), ';', ','), ']')]"
                              }
                            ]
                          }
                        }
                      },
                      {
                        "apiVersion": "2017-03-30",
                        "type": "Microsoft.Compute/virtualMachines",
                        "identity": {
                          "type": "SystemAssigned"
                        },
                        "name": "[parameters('vmName')]",
                        "location": "[parameters('location')]"
                      },
                      {
                        "apiVersion": "2015-05-01-preview",
                        "name": "[concat(parameters('vmName'), '/AzurePolicyforLinux')]",
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "location": "[parameters('location')]",
                        "properties": {
                          "publisher": "Microsoft.GuestConfiguration",
                          "type": "ConfigurationforLinux",
                          "typeHandlerVersion": "1.0",
                          "autoUpgradeMinorVersion": true
                        },
                        "dependsOn": [
                          "[concat('Microsoft.Compute/virtualMachines/',parameters('vmName'),'/providers/Microsoft.GuestConfiguration/guestConfigurationAssignments/',parameters('configurationName'))]"
                        ]
                      }
                    ]
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4d1c04de-2172-403f-901b-90608c35c721",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4d1c04de-2172-403f-901b-90608c35c721"
    },
    {
      "properties": {
        "displayName": "[Preview]: Deploy Dependency Agent for Linux VMs",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploy Dependency Agent for Linux VMs if the VM Image (OS) is in the list defined and the agent is not installed.",
        "metadata": {
          "category": "Monitoring"
        },
        "parameters": {
          "listOfImageIdToInclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Optional: List of VM images that have supported Linux OS to add to scope",
              "description": "Example value: '/subscriptions/<subscriptionId>/resourceGroups/YourResourceGroup/providers/Microsoft.Compute/images/ContosoStdImage'"
            },
            "defaultValue": []
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/imageId",
                    "in": "[parameters('listOfImageIdToInclude')]"
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "UbuntuServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "in": [
                              "14.04.0-LTS",
                              "14.04.1-LTS",
                              "14.04.5-LTS"
                            ]
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "in": [
                              "16.04-LTS",
                              "16.04.0-LTS"
                            ]
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "in": [
                              "18.04-LTS"
                            ]
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "RHEL",
                          "RHEL-SAP-HANA"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "6.*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "7*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "SUSE"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "SLES",
                          "SLES-HPC",
                          "SLES-HPC-Priority",
                          "SLES-SAP",
                          "SLES-SAP-BYOS",
                          "SLES-Priority",
                          "SLES-BYOS",
                          "SLES-SAPCAL",
                          "SLES-Standard"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "in": [
                              "12-SP2",
                              "12-SP3"
                            ]
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "OpenLogic"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "CentOS",
                          "Centos-LVM",
                          "CentOS-SRIOV"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "6.*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "like": "7*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "cloudera"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "cloudera-centos-os"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "like": "7*"
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/92aaf0da-9dab-42b6-94a3-d43ce8d16293"
              ],
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "DependencyAgentLinux"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "Microsoft.Azure.Monitoring.DependencyAgent"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/provisioningState",
                    "equals": "Succeeded"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "variables": {
                      "vmExtensionName": "DependencyAgent",
                      "vmExtensionPublisher": "Microsoft.Azure.Monitoring.DependencyAgent",
                      "vmExtensionType": "DependencyAgentLinux",
                      "vmExtensionTypeHandlerVersion": "9.6"
                    },
                    "resources": [
                      {
                        "type": "Microsoft.Compute/virtualMachines/extensions",
                        "name": "[concat(parameters('vmName'), '/', variables('vmExtensionName'))]",
                        "apiVersion": "2018-06-01",
                        "location": "[parameters('location')]",
                        "properties": {
                          "publisher": "[variables('vmExtensionPublisher')]",
                          "type": "[variables('vmExtensionType')]",
                          "typeHandlerVersion": "[variables('vmExtensionTypeHandlerVersion')]",
                          "autoUpgradeMinorVersion": true
                        }
                      }
                    ],
                    "outputs": {
                      "policy": {
                        "type": "string",
                        "value": "[concat('Enabled extension for VM', ': ', parameters('vmName'))]"
                      }
                    }
                  },
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4da21710-ce6f-4e06-8cdb-5cc4c93ffbee",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4da21710-ce6f-4e06-8cdb-5cc4c93ffbee"
    },
    {
      "properties": {
        "displayName": "A maximum of 3 owners should be designated for your subscription",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "It is recommended to designate up to 3 subscription owners in order to reduce the potential for breach by a compromised owner.",
        "metadata": {
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Resources/subscriptions"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/complianceResults",
              "name": "DesignateLessThanXOwners",
              "existenceCondition": {
                "field": "Microsoft.Security/complianceResults/resourceStatus",
                "in": [
                  "NotApplicable",
                  "OffByPolicy",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
