      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "e9c8d085-d9cc-4b17-9cdc-059f1f01f19e"
    },
    {
      "properties": {
        "displayName": "Deprecated accounts with owner permissions should be removed from your subscription",
