    {
      "properties": {
        "displayName": "[cstack] Keyvault-Good",
        "policyType": "Custom",
        "mode": "All",
        "description": "Policy to add access policies to keyvault - take 2. Works by adding access policies and deploying at the vault level.",
        "metadata": {
          "category": "cstack",
          "createdBy": "3d826307-2481-45a0-a271-bcf9333f914a",
          "createdOn": "2019-03-23T01:50:51.5383838Z",
          "updatedBy": "3d826307-2481-45a0-a271-bcf9333f914a",
          "updatedOn": "2019-03-26T22:01:30.8986918Z"
        },
        "parameters": {
          "userObjectId": {
            "type": "String",
            "metadata": {
              "displayName": "User Object ID",
              "description": "The GUID for the user which should have access"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.KeyVault/vaults"
              },
              {
                "field": "Microsoft.Keyvault/vaults/accessPolicies[*].objectId",
                "notEquals": "[parameters('userObjectId')]"
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.KeyVault/vaults",
              "name": "current",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/f25e0fa2-a7c8-4377-a976-54943a77a395"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "objectId": {
                        "type": "string"
                      },
                      "keyVaultName": {
                        "type": "string"
                      },
                      "secretsPermissions": {
                        "type": "array",
                        "defaultValue": [
                          "list"
                        ]
                      },
                      "tenantId": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "sku": {
                        "type": "object"
                      },
                      "existingAccessPolicies": {
                        "type": "array",
                        "defaultValue": []
                      }
                    },
                    "variables": {
                      "accessPolicies": [
                        {
                          "tenantId": "[parameters('tenantId')]",
                          "objectId": "[parameters('objectId')]",
                          "permissions": {
                            "secrets": "[parameters('secretsPermissions')]"
                          }
                        }
                      ]
                    },
                    "resources": [
                      {
                        "type": "Microsoft.KeyVault/vaults",
                        "name": "[parameters('keyVaultName')]",
                        "location": "[parameters('location')]",
                        "apiVersion": "2018-02-14",
                        "properties": {
                          "sku": "[parameters('sku')]",
                          "tenantId": "[parameters('tenantId')]",
                          "accessPolicies": "[concat(parameters('existingAccessPolicies'), variables('accessPolicies'))]"
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "objectId": {
                      "value": "[parameters('userObjectId')]"
                    },
                    "tenantId": {
                      "value": "[field('Microsoft.Keyvault/vaults/tenantId')]"
                    },
                    "keyVaultName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "sku": {
                      "value": "[field('Microsoft.Keyvault/vaults/sku')]"
                    },
                    "existingAccessPolicies": {
                      "value": "[field('Microsoft.Keyvault/vaults/accessPolicies')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
