    {
      "properties": {
        "displayName": "Append tag and its default value with Guid and UTC time",
        "policyType": "Custom",
        "mode": "Indexed",
        "description": "On a new or updated resource without the specified tag, appends that tag and its default value. Does not apply to resource groups.",
        "metadata": {
          "category": "General",
          "createdBy": "3adec713-6bc1-4bc6-8283-b0c71d198f90",
          "createdOn": "2019-05-10T03:37:32.1823573Z",
          "updatedBy": "3adec713-6bc1-4bc6-8283-b0c71d198f90",
          "updatedOn": "2019-05-11T07:17:06.821363Z"
        },
        "parameters": {
          "tagName": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Name of the tag, such as 'environment'"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "[concat('tags[', parameters('tagName'), ']')]",
            "exists": "false"
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "[concat('tags[', parameters('tagName'), ']')]",
                "value": "[concat(guid(subscription().id, resourceGroup().Name, field('type'), field('name'), utcNow()), '-', utcNow())]"
              }
            ]
          }
        }
      },
