    {
      "properties": {
        "displayName": "Deploy security rule with restricted source IPs",
        "policyType": "Custom",
        "mode": "All",
        "description": "Ovewrites security rules with IP restrictions at the securityRule level",
        "metadata": {
          "category": "Port Lockdown"
        },
        "parameters": {
          "destinationPortRanges": {
            "type": "Array",
            "metadata": {
              "displayName": "Destination port ranges",
              "description": "Destination port ranges requiring IP restrictions. Limit to one protocol. The most inclusive should come last. I.e. 22;22-22;22-23"
            }
          },
          "sourceAddressPrefixes": {
            "type": "Array",
            "metadata": {
              "displayName": "IP restriction prefixes",
              "description": "The IP ranges incoming traffic will be restricted to. I.e. 192.4.0.0/8;192.5.0.0/8"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/networkSecurityGroups/securityRules"
              },
              {
                "allOf": [
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Network/networkSecurityGroups/securityRules/destinationPortRange",
                        "in": "[parameters('destinationPortRanges')]"
                      },
                      {
                        "not": {
                          "field": "Microsoft.Network/networkSecurityGroups/securityRules/destinationPortRanges[*]",
                          "notIn": "[parameters('destinationPortRanges')]"
                        }
                      }
                    ]
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/access",
                    "equals": "Allow"
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/direction",
                    "equals": "Inbound"
                  },
                  {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                            "exists": "true"
                          },
                          {
                            "not": {
                              "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                              "notLike": "*"
                            }
                          },
                          {
                            "not": {
                              "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                              "notIn": [
                                "*",
                                "Internet"
                              ]
                            }
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                            "notEquals": "VirtualNetwork"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "exists": "true"
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "notEquals": ""
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "in": [
                              "*",
                              "Internet"
                            ]
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "notEquals": "VirtualNetwork"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                            "equals": ""
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "equals": ""
                          }
                        ]
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Authorization/policyAssignments",
              "name": "DoesNotExist101",
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "fullRuleName": {
                        "type": "string"
                      },
                      "sourceAddressPrefixes": {
                        "type": "array"
                      },
                      "destinationPortRanges": {
                        "type": "array"
                      },
                      "priority": {
                        "type": "int"
                      }
                    },
                    "resources": [
                      {
                        "name": "[parameters('fullRuleName')]",
                        "type": "Microsoft.Network/networkSecurityGroups/securityRules",
                        "apiVersion": "2018-02-01",
                        "properties": {
                          "protocol": "*",
                          "sourceAddressPrefixes": "[parameters('sourceAddressPrefixes')]",
                          "destinationAddressPrefix": "*",
                          "sourcePortRange": "*",
                          "destinationPortRange": "[last(parameters('destinationPortRanges'))]",
                          "access": "Allow",
                          "direction": "Inbound",
                          "priority": "[parameters('priority')]"
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "fullRuleName": {
                      "value": "[field('fullName')]"
                    },
                    "sourceAddressPrefixes": {
                      "value": "[parameters('sourceAddressPrefixes')]"
                    },
                    "destinationPortRanges": {
                      "value": "[parameters('destinationPortRanges')]"
                    },
                    "priority": {
                      "value": "[field('Microsoft.Network/networksecurityGroups/securityRules/priority')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/policyDefinitions/20c4afd0-8a77-4433-b8b0-4ad06e4c7111",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "20c4afd0-8a77-4433-b8b0-4ad06e4c7111"
    },
    {
      "properties": {
        "displayName": "Deploy NSGs on Subnets",
        "policyType": "Custom",
        "mode": "All",
        "description": "Enforce that all subnets have a Network Security Group.  If a subnet does not have one an NSG with the default Internet Exposed Endpoint restrictions will be created and associated with it.",
        "metadata": {
          "category": "Port Lockdown"
        },
        "parameters": {
          "nsgPrefix": {
            "type": "String",
            "metadata": {
              "displayName": "NSG name prefix",
              "description": "The prefix string to apply to all automatically created network security groups."
            }
          },
          "sourceAddressPrefixes": {
            "type": "Array",
            "metadata": {
              "displayName": "IP restriction prefixes",
              "description": "The IP ranges incoming traffic will be restricted to. I.e. 192.4.0.0/8;192.5.0.0/8"
            }
          },
          "destinationPortRanges": {
            "type": "Array",
            "metadata": {
              "displayName": "Destination port ranges",
              "description": "Destination port ranges requiring IP restrictions. Ranges must not overlap."
            }
          }
        },
        "policyRule": {
          "if": {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Network/virtualNetworks/subnets"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Network/virtualNetworks/subnets/networkSecurityGroup.id",
                        "exists": "false"
                      },
                      {
                        "field": "Microsoft.Network/virtualNetworks/subnets/networkSecurityGroup.id",
                        "equals": ""
                      }
                    ]
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "field": "type",
                    "equals": "Microsoft.Network/virtualNetworks"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Network/virtualNetworks/subnets[*].networkSecurityGroup.id",
                        "exists": "false"
                      },
                      {
                        "not": {
                          "field": "Microsoft.Network/virtualNetworks/subnets[*].networkSecurityGroup.id",
                          "notEquals": "null"
                        }
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Authorization/policyAssignments",
              "name": "DoesNotExist101",
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "fullResourceName": {
                        "type": "string"
                      },
                      "resourceName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "nsgPrefix": {
                        "type": "string"
                      },
                      "sourceAddressPrefixes": {
                        "type": "array"
                      },
                      "destinationPortRanges": {
                        "type": "array"
                      }
                    },
                    "variables": {
                      "nsgName": "[concat(parameters('nsgPrefix'), '-', parameters('location'))]",
                      "vnetName": "[split(parameters('fullResourceName'), '/')[0]]",
                      "vnetResourceId": "[resourceId('Microsoft.Network/virtualNetworks', variables('vnetName'))]",
                      "getVnetDeploymentName": "[concat('getVnet-', variables('vnetName'))]",
                      "collectSubnetsDeploymentName": "[concat('collectSubnets-', variables('vnetName'))]",
                      "overwriteVnetDeploymentName": "[concat('overwriteVnet-', variables('vnetName'))]"
                    },
                    "resources": [
                      {
                        "apiVersion": "2018-02-01",
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('getVnetDeploymentName')]",
                        "properties": {
                          "mode": "Incremental",
                          "template": {
                            "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                            "contentVersion": "1.0.0.0",
                            "resources": [],
                            "outputs": {
                              "vnetProperties": {
                                "type": "object",
                                "value": "[reference(variables('vnetResourceId'), '2018-03-01', 'Full')]"
                              }
                            }
                          }
                        }
                      },
                      {
                        "name": "[variables('nsgName')]",
                        "type": "Microsoft.Network/networkSecurityGroups",
                        "apiVersion": "2018-03-01",
                        "location": "[parameters('location')]",
                        "properties": {
                          "securityRules": [
                            {
                              "name": "PortLockdown_ControlledPorts_Restrict",
                              "properties": {
                                "description": "Allow controlled port connections from specific IP ranges (either corpnet or SAW)",
                                "protocol": "*",
                                "sourcePortRange": "*",
                                "destinationPortRange": "",
                                "destinationPortRanges": "[parameters('destinationPortRanges')]",
                                "sourceAddressPrefix": "",
                                "sourceAddressPrefixes": "[parameters('sourceAddressPrefixes')]",
                                "destinationAddressPrefix": "*",
                                "access": "Allow",
                                "priority": 3997,
                                "direction": "Inbound"
                              }
                            },
                            {
                              "name": "PortLockdown_ControlledPorts_AllowVnet",
                              "properties": {
                                "description": "Allow controlled port connections from within the VNET",
                                "protocol": "*",
                                "sourcePortRange": "*",
                                "destinationPortRange": "",
                                "destinationPortRanges": "[parameters('destinationPortRanges')]",
                                "sourceAddressPrefix": "VirtualNetwork",
                                "destinationAddressPrefix": "*",
                                "access": "Allow",
                                "priority": 3998,
                                "direction": "Inbound"
                              }
                            },
                            {
                              "name": "PortLockdown_ControlledPorts_Deny",
                              "properties": {
                                "description": "Deny any controlled port connections that aren't explicitly allowed in higher priority rules",
                                "protocol": "*",
                                "sourcePortRange": "*",
                                "destinationPortRange": "",
                                "destinationPortRanges": "[parameters('destinationPortRanges')]",
                                "sourceAddressPrefix": "*",
                                "destinationAddressPrefix": "*",
                                "access": "Deny",
                                "priority": 3999,
                                "direction": "Inbound"
                              }
                            },
                            {
                              "name": "PortLockdown_AllowAll",
                              "properties": {
                                "description": "Allow all inbound traffic that isn't explicitly blocked by Port Lockdown restrictions",
                                "protocol": "*",
                                "sourcePortRange": "*",
                                "destinationPortRange": "*",
                                "sourceAddressPrefix": "*",
                                "destinationAddressPrefix": "*",
                                "access": "Allow",
                                "priority": 4000,
                                "direction": "Inbound"
                              }
                            }
                          ]
                        }
                      },
                      {
                        "apiVersion": "2018-02-01",
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('collectSubnetsDeploymentName')]",
                        "properties": {
                          "mode": "Incremental",
                          "templateLink": {
                            "uri": "https://portlockdown.blob.core.windows.net/portlockdown-templates/Subnet_OverwriteWithNsg_collectSubnets_template.json",
                            "contentVersion": "1.0.0.0"
                          },
                          "parameters": {
                            "vnetProperties": {
                              "value": "[reference(variables('getVnetDeploymentName')).outputs.vnetProperties.value]"
                            },
                            "nsgResourceId": {
                              "value": "[resourceid('Microsoft.Network/networkSecurityGroups', variables('nsgName'))]"
                            },
                            "vnetName": {
                              "value": "[variables('vnetName')]"
                            }
                          }
                        }
                      },
                      {
                        "dependsOn": [
                          "[variables('nsgName')]"
                        ],
                        "apiVersion": "2018-02-01",
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('overwriteVnetDeploymentName')]",
                        "properties": {
                          "mode": "Incremental",
                          "templateLink": {
                            "uri": "https://portlockdown.blob.core.windows.net/portlockdown-templates/Subnet_OverwriteWithNsg_overwriteVnet_template.json",
                            "contentVersion": "1.0.0.0"
                          },
                          "parameters": {
                            "vnetProperties": {
                              "value": "[reference(variables('getVnetDeploymentName')).outputs.vnetProperties.value]"
                            },
                            "location": {
                              "value": "[parameters('location')]"
                            },
                            "updatedSubnets": {
                              "value": "[reference(variables('collectSubnetsDeploymentName')).outputs.updatedSubnets.value]"
                            },
                            "vnetName": {
                              "value": "[variables('vnetName')]"
                            }
                          }
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "fullResourceName": {
                      "value": "[field('fullName')]"
                    },
                    "resourceName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    },
                    "nsgPrefix": {
                      "value": "[parameters('nsgPrefix')]"
                    },
                    "sourceAddressPrefixes": {
                      "value": "[parameters('sourceAddressPrefixes')]"
                    },
                    "destinationPortRanges": {
                      "value": "[parameters('destinationPortRanges')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
