    {
      "properties": {
        "displayName": "[demo] Enforce KeyVault diagnostic log storage",
        "policyType": "Custom",
        "mode": "All",
        "description": "Ensures that AuditEvents are collected from all Key Vaults in the subscription and stored in a specific storage account for 30 days.",
        "metadata": {
          "category": "Key Vault",
          "createdBy": null,
          "createdOn": null,
          "updatedBy": "0dc80135-ae53-4da3-8695-220a2d93aad8",
          "updatedOn": "2019-03-12T18:54:40.6304518Z"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.KeyVault/vaults"
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                "equals": "true"
              },
              "roleDefinitionIds": [
                "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/roleDefinitions/749f88d5-cbae-40b8-bcfc-e573ddc772fa",
                "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/roleDefinitions/17d1049b-9a84-46fb-8f53-869881c3d3ab"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "variables": {
                      "storageName": "[tolower(concat('cheggkv', parameters('location')))]"
                    },
                    "resources": [
                      {
                        "type": "Microsoft.KeyVault/vaults/providers/diagnosticSettings",
                        "name": "[concat(parameters('vaultName'),'/Microsoft.Insights/setbypolicy')]",
                        "apiVersion": "2017-05-01-preview",
                        "location": "[parameters('location')]",
                        "properties": {
                          "storageAccountId": "[resourceid('cheggremdemo', 'Microsoft.Storage/storageAccounts', variables('storageName'))]",
                          "logs": [
                            {
                              "category": "AuditEvent",
                              "enabled": true,
                              "retentionPolicy": {
                                "enabled": true,
                                "days": 30
                              }
                            }
                          ]
                        }
                      }
                    ],
                    "parameters": {
                      "vaultName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    }
                  },
                  "parameters": {
                    "vaultName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
