    {
      "properties": {
        "displayName": "nrms-nsg-rule-102_1.0",
        "policyType": "Custom",
        "mode": "All",
        "metadata": {
          "category": "camarvin",
          "createdBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "createdOn": "2019-05-22T21:56:17.5500929Z",
          "updatedBy": null,
          "updatedOn": null
        },
        "parameters": {
          "allowedLocations": {
            "type": "Array",
            "metadata": {
              "displayName": "Allowed locations",
              "description": "The list of locations that can be specified when deploying resources.",
              "strongType": "location"
            }
          },
          "ruleNo": {
            "type": "String",
            "metadata": {
              "displayName": "Priority",
              "description": "The priority slot for the new rule"
            },
            "allowedValues": [
              "100",
              "101",
              "102",
              "103",
              "104",
              "105",
              "106",
              "107",
              "108",
              "109"
            ]
          },
          "info": {
            "type": "String",
            "metadata": {
              "displayName": "Description",
              "description": "A description"
            }
          },
          "destinationPortRanges": {
            "type": "Array",
            "metadata": {
              "displayName": "Port Number",
              "description": "Security rule port numbers must match these numbers"
            }
          },
          "protocol": {
            "type": "String",
            "metadata": {
              "displayName": "protocol",
              "description": "Protocol for the rule"
            },
            "allowedValues": [
              "*",
              "Tcp",
              "Udp"
            ]
          },
          "sourceType": {
            "type": "String",
            "metadata": {
              "displayName": "sourceType",
              "description": null
            },
            "allowedValues": [
              "IP Addresses",
              "Service Tag",
              "App Security Group"
            ]
          },
          "sourceValue": {
            "type": "String",
            "metadata": {
              "displayName": "sourceValue",
              "description": null
            }
          },
          "actionValue": {
            "type": "String",
            "metadata": {
              "displayName": "actionValue",
              "description": null
            },
            "allowedValues": [
              "Allow",
              "Deny"
            ]
          },
          "direction": {
            "type": "String",
            "metadata": {
              "displayName": "direction",
              "description": null
            },
            "allowedValues": [
              "Inbound",
              "Outbound"
            ]
          },
          "tagname": {
            "type": "String",
            "metadata": {
              "displayName": "Tag Name",
              "description": "Rule is not deployed if this tag exists on the NSG"
            },
            "allowedValues": [
              "NA",
              "NRMSException",
              "SkipNRMSCorp",
              "nrmsskipcorpnetsaw",
              "SkipNRMSSAW",
              "SkipNRMSDatabricks",
              "SkipNRMSRDPSSH",
              "SkipNRMSMgmt",
              "SkipNRMSHigh",
              "SkipNRMSVNet",
              "SkipNRMSLoadBal"
            ],
            "defaultValue": "NA"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/networkSecurityGroups"
              },
              {
                "field": "location",
                "in": "[parameters('allowedLocations')]"
              },
              {
                "field": "[concat('tags[', parameters('tagName'), ']')]",
                "exists": "false"
              },
              {
                "field": "tags['SkipNRMSAll']",
                "exists": "false"
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Network/networkSecurityGroups/securityRules",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/4d97b98b-1d4f-4787-a291-c67834d212e7"
              ],
              "existenceCondition": {
                "allOf": [
                  {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "fullName",
                            "equals": "[concat(field('name'),'/','NRMS-Rule-', parameters('ruleNo'))]"
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/priority",
                            "equals": "[parameters('ruleno')]"
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "equals": "[parameters('sourceValue')]"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "fullName",
                            "equals": "Cleanuptool-Deny-103"
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/priority",
                            "equals": "103"
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "equals": "Any"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/access",
                    "equals": "Deny"
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/direction",
                    "equals": "Inbound"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "fullName": {
                        "type": "string"
                      },
                      "ruleNo": {
                        "type": "String"
                      },
                      "destinationPortRanges": {
                        "type": "Array"
                      },
                      "protocol": {
                        "type": "String"
                      },
                      "sourceType": {
                        "type": "String"
                      },
                      "sourceValue": {
                        "type": "String"
                      },
                      "actionValue": {
                        "type": "String"
                      },
                      "direction": {
                        "type": "String"
                      },
                      "info": {
                        "type": "String"
                      }
                    },
                    "variables": {
                      "SourceArr": "[split(parameters('sourceValue'),',')]",
                      "appSecGroups": {
                        "copy": [
                          {
                            "name": "values",
                            "count": "[length(variables('SourceArr'))]",
                            "input": {
                              "id": "[if(equals(parameters('sourceType'),'App Security Group'),if(contains(variables('SourceArr')[copyindex('values')],'subscriptions'), variables('SourceArr')[copyindex('values')],resourceId('Microsoft.Network/applicationSecurityGroups', variables('SourceArr')[copyindex('values')])), '')]"
                            }
                          }
                        ]
                      }
                    },
                    "resources": [
                      {
                        "type": "Microsoft.Network/networkSecurityGroups/securityRules",
                        "name": "[concat(parameters('fullName'),'/','NRMS-Rule-', parameters('ruleNo'))]",
                        "apiVersion": "2017-10-01",
                        "scale": null,
                        "properties": {
                          "protocol": "[parameters('protocol')]",
                          "description": "[parameters('info')]",
                          "sourcePortRange": "*",
