    {
      "properties": {
        "displayName": "Storage Lifecycle management audit policy",
        "policyType": "Custom",
        "mode": "All",
        "description": "Checks whether lifecycle management policy is present under the storage account",
        "metadata": {
          "category": "Storage",
          "createdBy": "55899cf9-827a-4788-bed4-c0d5c16f943a",
          "createdOn": "2019-04-11T21:40:47.5959278Z",
          "updatedBy": "55899cf9-827a-4788-bed4-c0d5c16f943a",
          "updatedOn": "2019-04-12T00:40:10.2404999Z"
        },
        "parameters": {},
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Storage/storageAccounts"
              },
              {
                "field": "kind",
                "in": [
                  "BlobStorage",
                  "StorageV2"
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Storage/storageAccounts/managementPolicies",
              "name": "default",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/17d1049b-9a84-46fb-8f53-869881c3d3ab"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "storageAccountName": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('storageAccountName'),'/default')]",
                        "type": "Microsoft.Storage/storageAccounts/managementPolicies",
                        "apiVersion": "2018-11-01",
                        "properties": {
                          "policy": {
                            "rules": [
                              {
                                "name": "olcmtest",
                                "type": "Lifecycle",
                                "definition": {
                                  "filters": {
                                    "blobTypes": [
                                      "blockBlob"
                                    ],
                                    "prefixMatch": [
                                      "container1/foo"
                                    ]
                                  },
                                  "actions": {
                                    "baseBlob": {
                                      "tierToCool": {
                                        "daysAfterModificationGreaterThan": 30
                                      },
                                      "tierToArchive": {
                                        "daysAfterModificationGreaterThan": 90
                                      },
                                      "delete": {
                                        "daysAfterModificationGreaterThan": 180
                                      }
                                    },
                                    "snapshot": {
                                      "delete": {
                                        "daysAfterCreationGreaterThan": 90
                                      }
                                    }
                                  }
                                }
                              }
                            ]
                          }
                        }
                      }
                    ],
                    "outputs": {
                      "policy": {
                        "type": "string",
                        "value": "[concat('Enabled blob lifecycle management policy for storage account', ': ', parameters('storageAccountName'))]"
                      }
                    }
                  },
                  "parameters": {
                    "storageAccountName": {
                      "value": "[field('name')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
