    {
      "properties": {
        "displayName": "[cstack] Keyvault-Bad",
        "policyType": "Custom",
        "mode": "All",
        "description": "Designed to add access policies by deploying at the access policy level. But it doesn't work, since it needs to treat Microsoft.Keyvault/vaults/accessPolicies as a type.",
        "metadata": {
          "category": "cstack",
          "createdBy": "3d826307-2481-45a0-a271-bcf9333f914a",
          "createdOn": "2019-03-19T22:09:01.1382535Z",
          "updatedBy": "3d826307-2481-45a0-a271-bcf9333f914a",
          "updatedOn": "2019-03-26T22:03:43.8712334Z"
        },
        "parameters": {
          "userObjectId": {
            "type": "String",
            "metadata": {
              "displayName": "User Object ID",
              "description": "The GUID for the user which should have access"
            }
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.KeyVault/vaults"
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.KeyVault/vaults",
              "name": "current",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
              ],
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Keyvault/vaults/accessPolicies",
                    "exists": "true"
                  },
                  {
                    "not": {
                      "field": "Microsoft.Keyvault/vaults/accessPolicies[*].objectId",
                      "equals": "[parameters('userObjectId')]"
                    }
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "objectId": {
                        "type": "string"
                      },
                      "keyVaultName": {
                        "type": "string"
                      },
                      "secretsPermissions": {
                        "type": "array",
                        "defaultValue": [
                          "list"
                        ]
                      },
                      "tenantId": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "type": "Microsoft.KeyVault/vaults/accessPolicies",
                        "name": "[concat(parameters('keyVaultName'), '/add')]",
                        "apiVersion": "2018-02-14",
                        "properties": {
                          "accessPolicies": [
                            {
                              "tenantId": "[parameters('tenantId')]",
                              "objectId": "[parameters('objectId')]",
                              "permissions": {
                                "secrets": "[parameters('secretsPermissions')]"
                              }
                            }
                          ]
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "objectId": {
                      "value": "[parameters('userObjectId')]"
                    },
                    "tenantId": {
                      "value": "[field('Microsoft.Keyvault/vaults/tenantId')]"
                    },
                    "keyVaultName": {
                      "value": "[field('name')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
