    {
      "properties": {
        "displayName": "ATS-POL-Append-ApplyRGappToglobal-app",
        "policyType": "Custom",
        "mode": "Indexed",
        "description": "Append the 'global-app' Tag to a Resource with the value of the 'RGapp' Tag of its Resource Group.",
        "metadata": {
          "category": "GO Standard",
          "createdBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "createdOn": "2019-03-16T03:02:38.4294801Z",
          "updatedBy": null,
          "updatedOn": null
        },
        "parameters": {
          "effectType": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Choose 'Disabled' to disable the execution of the policy and the Append effect"
            },
            "allowedValues": [
              "Append",
              "Disabled"
            ]
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "tags",
                "notContainsKey": "global-app"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effectType')]",
            "details": [
              {
                "field": "tags.global-app",
                "value": "[resourceGroup().tags.rgapp]"
              }
            ]
          }
        }
      },
      "id": "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/policyDefinitions/ATS-POL-Append-ApplyRGappToglobal-app",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "ATS-POL-Append-ApplyRGappToglobal-app"
    },
    {
      "properties": {
        "displayName": "Audit if extension does not exist",
        "policyType": "Custom",
        "mode": "All",
        "description": "This policy audits if a required extension doesn't exist.",
        "parameters": {
          "publisher": {
            "type": "String",
            "metadata": {
              "description": "The publisher of the extension",
              "displayName": "Extension Publisher"
            }
          },
          "type": {
            "type": "String",
            "metadata": {
              "description": "The type of the extension",
              "displayName": "Extension Type"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "field": "Microsoft.Compute/imagePublisher",
                "in": [
                  "MicrosoftWindowsServer"
                ]
              },
              {
                "field": "Microsoft.Compute/imageOffer",
                "in": [
                  "WindowsServer"
                ]
              }
            ]
          },
          "then": {
            "effect": "auditIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachines/extensions",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/publisher",
                    "equals": "[parameters('publisher')]"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachines/extensions/type",
                    "equals": "[parameters('type')]"
                  }
                ]
              }
            }
          }
        }
      },
      "id": "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/policyDefinitions/audit-vm-extension",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "audit-vm-extension"
    },
    {
      "properties": {
        "policyType": "Custom",
        "mode": "All",
        "metadata": {
          "createdBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "createdOn": "2019-03-15T21:13:27.1283401Z",
          "updatedBy": null,
          "updatedOn": null
        },
        "parameters": {
          "allowedLocations": {
            "type": "Array",
            "metadata": {
              "displayName": "Allowed locations",
              "description": "The list of locations that can be specified when deploying resources.",
              "strongType": "location"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachineScaleSets"
              },
              {
                "field": "location",
                "in": "[parameters('allowedLocations')]"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/VirtualMachineScaleSets/osProfile.windowsConfiguration",
                    "exists": "true"
                  },
                  {
                    "field": "Microsoft.Compute/imagePublisher",
                    "equals": "MicrosoftWindowsServer"
                  }
                ]
              },
              {
                "field": "Microsoft.Compute/VirtualMachineScaleSets/virtualMachineProfile",
                "exists": "true"
              },
              {
                "field": "Microsoft.Compute/VirtualMachineScaleSets/extensionProfile.extensions[*].type",
                "notEquals": "GenevaMonitoring"
              }
            ]
          },
          "then": {
            "effect": "append",
            "details": [
              {
                "field": "Microsoft.Compute/VirtualMachineScaleSets/extensionProfile.extensions[*]",
                "value": {
                  "name": "Microsoft.Azure.Geneva.GenevaMonitoring",
                  "properties": {
                    "publisher": "Microsoft.Azure.Geneva",
                    "type": "GenevaMonitoring",
                    "typeHandlerVersion": "1.8",
                    "autoUpgradeMinorVersion": true,
                    "settings": {}
                  }
                }
              }
            ]
          }
        }
      },
      "id": "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/policyDefinitions/AzSecPack_VMSS_AppendPolicy",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "AzSecPack_VMSS_AppendPolicy"
    },
    {
      "properties": {
        "policyType": "Custom",
        "mode": "Indexed",
        "metadata": {
          "createdBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "createdOn": "2019-03-15T21:11:49.9295069Z",
          "updatedBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "updatedOn": "2019-03-15T21:12:57.3336548Z"
        },
        "parameters": {
          "allowedLocations": {
            "type": "Array",
            "metadata": {
              "displayName": "Allowed locations",
              "description": "The list of locations that can be specified when deploying resources.",
              "strongType": "location"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachineScaleSets"
              },
              {
                "field": "location",
                "in": "[parameters('allowedLocations')]"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/VirtualMachineScaleSets/osProfile.windowsConfiguration",
                    "exists": "true"
                  },
                  {
                    "field": "Microsoft.Compute/imagePublisher",
                    "equals": "MicrosoftWindowsServer"
                  }
                ]
              },
              {
                "not": {
                  "field": "Microsoft.Compute/VirtualMachineScaleSets/extensionProfile.extensions[*].name",
                  "notEquals": "Microsoft.Azure.Geneva.GenevaMonitoring"
                }
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Compute/virtualMachineScaleSets/extensions",
              "name": "Microsoft.Azure.Geneva.GenevaMonitoring",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/type",
                    "equals": "GenevaMonitoring"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/publisher",
                    "equals": "Microsoft.Azure.Geneva"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/enableAutomaticUpgrade",
                    "equals": "true"
                  }
                ]
              },
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/9980e02c-c2be-4d73-94e8-173b1dc7cf3c"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmssName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "apiVersion": "2018-10-01",
                        "name": "[concat(parameters('vmssName'), '/Microsoft.Azure.Geneva.GenevaMonitoring')]",
                        "type": "Microsoft.Compute/virtualMachineScaleSets/extensions",
                        "location": "[parameters('location')]",
                        "properties": {
                          "publisher": "Microsoft.Azure.Geneva",
                          "type": "GenevaMonitoring",
                          "typeHandlerVersion": "1.8",
                          "autoUpgradeMinorVersion": true,
                          "enableAutomaticUpgrade": true,
                          "settings": {}
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "vmssName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/subscriptions/d0610b27-9663-4c05-89f8-5b4be01e86a5/providers/Microsoft.Authorization/policyDefinitions/AzSecPack_VMSS_EnableAutoUpgrade",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "AzSecPack_VMSS_EnableAutoUpgrade"
    },
