    {
      "properties": {
        "displayName": "Deploy security rule with restricted source IPs (NSG level)",
        "policyType": "Custom",
        "mode": "All",
        "description": "Overwrites security rules with IP restrictions at the NSG level",
        "metadata": {
          "category": "Port Lockdown"
        },
        "parameters": {
          "destinationPortRanges": {
            "type": "Array",
            "metadata": {
              "displayName": "Destination port ranges",
              "description": "Destination port ranges requiring IP restrictions. Ranges may overlap."
            }
          },
          "sourceAddressPrefixes": {
            "type": "Array",
            "metadata": {
              "displayName": "IP restriction prefixes",
              "description": "The IP ranges incoming traffic will be restricted to. I.e. 192.4.0.0/8;192.5.0.0/8"
            }
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/networkSecurityGroups"
              },
              {
                "anyOf": [
                  {
                    "not": {
                      "field": "Microsoft.Network/networkSecurityGroups/securityRules[*].destinationPortRange",
                      "notIn": "[parameters('destinationPortRanges')]"
                    }
                  },
                  {
                    "not": {
                      "field": "Microsoft.Network/networkSecurityGroups/securityRules[*].destinationPortRanges[*]",
                      "notIn": "[parameters('destinationPortRanges')]"
                    }
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "deployIfNotExists",
            "details": {
              "type": "Microsoft.Network/networkSecurityGroups/securityRules",
              "existenceCondition": {
                "allOf": [
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Network/networkSecurityGroups/securityRules/destinationPortRange",
                        "in": "[parameters('destinationPortRanges')]"
                      },
                      {
                        "not": {
                          "field": "Microsoft.Network/networkSecurityGroups/securityRules/destinationPortRanges[*]",
                          "notIn": "[parameters('destinationPortRanges')]"
                        }
                      }
                    ]
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/access",
                    "equals": "Allow"
                  },
                  {
                    "field": "Microsoft.Network/networkSecurityGroups/securityRules/direction",
                    "equals": "Inbound"
                  },
                  {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                            "exists": "true"
                          },
                          {
                            "not": {
                              "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                              "notLike": "*"
                            }
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefixes[*]",
                            "notIn": [
                              "*",
                              "Internet"
                            ]
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "exists": "true"
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "notEquals": ""
                          },
                          {
                            "field": "Microsoft.Network/networkSecurityGroups/securityRules/sourceAddressPrefix",
                            "notIn": [
                              "*",
                              "Internet"
                            ]
                          }
                        ]
                      }
                    ]
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "nsgName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "destinationPortRanges": {
                        "type": "array"
                      },
                      "sourceAddressPrefixes": {
                        "type": "array"
                      }
                    },
                    "variables": {
                      "getNsgDeploymentName": "[concat('getNSGContent-', parameters('nsgName'))]",
                      "collectorDeploymentName": "[concat('collectRules-', parameters('nsgName'))]",
                      "overwriteNsgDeploymentName": "[concat('overwriteNsg-', parameters('nsgName'))]",
                      "nsgResourceId": "[resourceId(subscription().subscriptionId, resourceGroup().name, 'Microsoft.Network/networkSecurityGroups', parameters('nsgName'))]"
                    },
                    "resources": [
                      {
                        "apiVersion": "2018-02-01",
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('getNsgDeploymentName')]",
                        "properties": {
                          "mode": "Incremental",
                          "template": {
                            "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                            "contentVersion": "1.0.0.0",
                            "resources": [],
                            "outputs": {
                              "nsgProperties": {
                                "type": "object",
                                "value": "[reference(variables('nsgResourceId'), '2018-03-01', 'Full')]"
                              }
                            }
                          }
                        }
                      },
                      {
                        "apiVersion": "2018-02-01",
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('collectorDeploymentName')]",
                        "properties": {
                          "mode": "Incremental",
                          "templateLink": {
                            "uri": "https://portlockdown.blob.core.windows.net/portlockdown-templates/SecurityRule_NSGLevel_collectRules_template.json",
                            "contentVersion": "1.0.0.0"
                          },
                          "parameters": {
                            "nsgProperties": {
                              "value": "[reference(variables('getNsgDeploymentName')).outputs.nsgProperties.value]"
                            },
                            "nsgName": {
                              "value": "[parameters('nsgName')]"
                            },
                            "location": {
                              "value": "[parameters('location')]"
                            },
                            "portRangesToRestrict": {
                              "value": "[parameters('destinationPortRanges')]"
                            },
                            "sourceAddressPrefixes": {
                              "value": "[parameters('sourceAddressPrefixes')]"
                            }
                          }
                        }
                      },
                      {
                        "apiVersion": "2018-02-01",
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('overwriteNsgDeploymentName')]",
                        "properties": {
                          "mode": "Incremental",
                          "templateLink": {
                            "uri": "https://portlockdown.blob.core.windows.net/portlockdown-templates/SecurityRule_NSGLevel_overwriteNSG_template.json",
                            "contentVersion": "1.0.0.0"
                          },
                          "parameters": {
                            "originalNsgProperties": {
