        "policyType": "Custom",
        "mode": "Indexed",
        "description": "SQL Server Audit Retention should be configured to be greater than 90 days.",
        "metadata": {
          "category": "camarvin",
          "createdBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "createdOn": "2019-05-15T23:14:58.6048916Z",
          "updatedBy": "3504496b-cbaa-4a69-97d8-97b9b3c6912e",
          "updatedOn": "2019-05-15T23:17:30.3440965Z"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Sql/servers"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Sql/servers/auditingSettings",
              "name": "default",
              "existenceCondition": {
                "field": "Microsoft.Sql/servers/auditingSettings/retentionDays",
                "greater": 90
              }
            }
          }
        }
      },
