      "id": "/providers/Microsoft.Authorization/policyDefinitions/2d60d3b7-aa10-454c-88a8-de39d99d17c6",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "2d60d3b7-aa10-454c-88a8-de39d99d17c6"
    },
    {
      "properties": {
        "displayName": "[Preview]: Audit Linux VM allowing remote connections from accounts with no passwords",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This security setting verifies remote connections from accounts with empty passwords is disabled.",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "anyOf": [
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "UbuntuServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "match": "14.04.#-LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "match": "16.04.#-LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "equals": "16.04-LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "match": "18.04-LTS"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Suse"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "SLES"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "equals": "12-SP3"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "RHEL"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "7-RAW",
                          "7.4"
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "credativ"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "Debian"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "9",
                          "8"
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "OpenLogic"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "CentOS"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "7.5",
                          "7.4",
                          "7.3"
                        ]
                      }
                    ]
                  }
                ]
              }
            ]
