    {
      "properties": {
        "displayName": "Deploy default Log Analytics Agent for Ubuntu VMs",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This policy deploys the Log Analytics Agent on Ubuntu VMs, and connects to the selected Log Analytics workspace",
        "metadata": {
          "category": "Compute",
          "deprecated": true
        },
        "parameters": {
          "logAnalytics": {
