                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "location": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "apiVersion": "2016-09-01",
                        "type": "Microsoft.Network/networkWatchers",
                        "name": "[concat('networkWacher_', parameters('location'))]",
