    },
    {
      "properties": {
        "displayName": "[Preview]: Audit Linux VM /etc/passwd file permissions are set to 0644",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "This security setting verifies /etc/passwd file permissions are set to 0644 to prevent unauthorized changes that could allow access to the server.",
        "metadata": {
          "category": "Guest Configuration"
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachines"
              },
              {
                "anyOf": [
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "UbuntuServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "match": "14.04.#-LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "match": "16.04.#-LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "equals": "16.04-LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSKU",
                            "match": "18.04-LTS"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Suse"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "SLES"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "equals": "12-SP3"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "RHEL"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "7-RAW",
                          "7.4"
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "credativ"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "Debian"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "9",
                          "8"
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "OpenLogic"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "CentOS"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "in": [
                          "7.5",
                          "7.4",
                          "7.3"
                        ]
                      }
                    ]
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "auditIfNotExists",
            "details": {
              "type": "Microsoft.GuestConfiguration/guestConfigurationAssignments",
              "name": "PasswordPolicy_msid121",
              "existenceCondition": {
                "field": "Microsoft.GuestConfiguration/guestConfigurationAssignments/complianceStatus",
                "equals": "Compliant"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/b18175dd-c599-4c64-83ba-bb018a06d35b",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "b18175dd-c599-4c64-83ba-bb018a06d35b"
    },
    {
      "properties": {
        "displayName": "Audit authorization rules on Event Hub namespaces",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Event Hub clients should not use a namespace level access policy that provides access to all queues and topics in a namespace. To align with the least privilege security model, you shoud create access policies at the entity level for queues and topics to provide access to only the specific entity",
        "metadata": {
          "category": "Event Hub"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.EventHub/namespaces/authorizationRules"
              },
              {
                "field": "name",
                "notEquals": "RootManageSharedAccessKey"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
