      "name": "b48334a4-911b-4084-b1ab-3e6a4e50b951"
    },
    {
      "properties": {
        "displayName": "Audit usage of Azure Active Directory for client authentication in Service Fabric",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit usage of client authentication only via Azure Active Directory in Service Fabric",
        "metadata": {
          "category": "Service Fabric"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.ServiceFabric/clusters"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.ServiceFabric/clusters/azureActiveDirectory.tenantId",
                    "exists": "false"
