              "displayName": "Applicable Locations",
              "description": "The list of locations where the policy should be applied.",
              "strongType": "location"
            },
            "allowedValues": [
              "australiasoutheast",
              "australiaeast",
              "brazilsouth",
              "canadacentral",
              "centralindia",
              "centralus",
              "eastasia",
              "eastus2euap",
              "eastus",
              "eastus2",
              "francecentral",
              "japaneast",
              "koreacentral",
              "northcentralus",
              "northeurope",
              "norwayeast",
              "southcentralus",
              "southeastasia",
              "switzerlandnorth",
              "uaenorth",
              "uksouth",
              "westcentralus",
              "westeurope",
              "westus",
              "westus2"
            ],
            "defaultValue": [
              "australiasoutheast",
              "australiaeast",
              "brazilsouth",
              "canadacentral",
              "centralindia",
              "centralus",
              "eastasia",
              "eastus2euap",
              "eastus",
              "eastus2",
              "francecentral",
              "japaneast",
              "koreacentral",
              "northcentralus",
              "northeurope",
              "norwayeast",
              "southcentralus",
              "southeastasia",
              "switzerlandnorth",
              "uaenorth",
              "uksouth",
              "westcentralus",
              "westeurope",
              "westus",
              "westus2"
            ]
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.HybridCompute/machines"
