            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "Disabled"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Web/sites"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
