            "allOf": [
              {
                "field": "Microsoft.DocumentDB/databaseAccounts/keyVaultKeyUri",
                "exists": false
              },
              {
                "field": "type",
                "equals": "Microsoft.DocumentDB/databaseAccounts"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
