    {
      "properties": {
        "displayName": "Configure Azure Automation accounts to disable public network access",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disable public network access for Azure Automation account so that it isn't accessible over the public internet. This configuration helps protect them against data leakage risks. You can limit exposure of the your Automation account resources by creating private endpoints instead. Learn more at: https://aka.ms/privateendpoints.",
        "metadata": {
          "category": "Automation",
          "version": "1.0.0"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Modify",
              "Disabled"
            ],
            "defaultValue": "Modify"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Automation/automationAccounts"
              },
              {
                "field": "Microsoft.Automation/automationAccounts/publicNetworkAccess",
                "notEquals": false
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "conflictEffect": "audit",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
              ],
              "operations": [
                {
                  "operation": "addOrReplace",
                  "field": "Microsoft.Automation/automationAccounts/publicNetworkAccess",
                  "value": false
                }
              ]
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/23b36a7c-9d26-4288-a8fd-c1d2fa284d8c",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "23b36a7c-9d26-4288-a8fd-c1d2fa284d8c"
    },
    {
      "properties": {
        "displayName": "Resource logs in Azure Kubernetes Service should be enabled",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Azure Kubernetes Service's resource logs can help recreate activity trails when investigating security incidents. Enable it to make sure the logs will exist when needed",
        "metadata": {
          "version": "1.0.0",
          "category": "Kubernetes"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "requiredRetentionDays": {
            "type": "String",
            "metadata": {
              "displayName": "Required retention (days)",
              "description": "The required resource logs retention (in days)"
            },
            "defaultValue": "365"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.ContainerService/managedClusters"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "count": {
                  "field": "Microsoft.Insights/diagnosticSettings/logs[*]",
                  "where": {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                            "equals": "true"
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days",
                                "equals": "0"
                              },
                              {
                                "value": "[padLeft(current('Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days'), 3, '0')]",
                                "greaterOrEquals": "[padLeft(parameters('requiredRetentionDays'), 3, '0')]"
                              }
                            ]
                          },
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                            "equals": "true"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                            "equals": "true"
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                                "notEquals": "true"
                              },
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/storageAccountId",
                                "exists": false
                              }
                            ]
                          }
                        ]
                      }
                    ]
                  }
                },
                "greaterOrEquals": 1
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/245fc9df-fa96-4414-9a0b-3738c2f7341c",
