          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.EventGrid/domains"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.EventGrid/domains/privateEndpointConnections",
              "existenceCondition": {
                "field": "Microsoft.EventGrid/domains/privateEndpointConnections/privateLinkServiceConnectionState.status",
                "equals": "Approved"
              },
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/4d97b98b-1d4f-4787-a291-c67834d212e7",
                "/providers/Microsoft.Authorization/roleDefinitions/1e241071-0855-49ea-94dc-649edcd759de"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "parameters": {
                    "name": {
                      "value": "[field('name')]"
                    },
                    "serviceId": {
                      "value": "[field('id')]"
                    },
                    "privateEndpointSubnetId": {
                      "value": "[parameters('privateEndpointSubnetId')]"
                    }
                  },
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2019-04-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "name": {
                        "type": "string"
                      },
                      "serviceId": {
                        "type": "string"
                      },
                      "privateEndpointSubnetId": {
                        "type": "string"
                      }
                    },
                    "variables": {
                      "privateEndpointName": "[concat('pe-',substring(parameters('name'),0,min(length(parameters('name')),50)),'-',uniquestring(deployment().name))]"
                    },
                    "resources": [
                      {
                        "type": "Microsoft.Resources/deployments",
                        "name": "[variables('privateEndpointName')]",
                        "apiVersion": "2020-06-01",
                        "properties": {
                          "mode": "Incremental",
                          "expressionEvaluationOptions": {
                            "scope": "inner"
                          },
                          "template": {
                            "$schema": "https://schema.management.azure.com/schemas/2019-04-01/deploymentTemplate.json#",
                            "contentVersion": "1.0.0.0",
                            "parameters": {
                              "serviceId": {
                                "type": "string"
                              },
                              "privateEndpointSubnetId": {
                                "type": "string"
                              },
                              "subnetLocation": {
                                "type": "string"
                              }
                            },
                            "variables": {
                              "privateEndpointName": "[deployment().name]"
                            },
                            "resources": [
                              {
                                "name": "[variables('privateEndpointName')]",
                                "type": "Microsoft.Network/privateEndpoints",
                                "apiVersion": "2020-07-01",
                                "location": "[parameters('subnetLocation')]",
                                "tags": {},
                                "properties": {
                                  "subnet": {
                                    "id": "[parameters('privateEndpointSubnetId')]"
                                  },
                                  "privateLinkServiceConnections": [
                                    {
                                      "name": "[variables('privateEndpointName')]",
                                      "properties": {
                                        "privateLinkServiceId": "[parameters('serviceId')]",
                                        "groupIds": [
                                          "domain"
                                        ],
                                        "requestMessage": "autoapprove"
                                      }
                                    }
                                  ],
                                  "manualPrivateLinkServiceConnections": []
                                }
                              }
                            ]
                          },
                          "parameters": {
                            "serviceId": {
                              "value": "[parameters('serviceId')]"
                            },
                            "privateEndpointSubnetId": {
                              "value": "[parameters('privateEndpointSubnetId')]"
                            },
                            "subnetLocation": {
                              "value": "[reference(first(take(split(parameters('privateEndpointSubnetId'),'/subnets'),1)),'2020-07-01','Full').location]"
                            }
                          }
                        }
                      }
                    ]
                  }
                }
              }
            }
          }
        }
      },
