    {
      "properties": {
        "displayName": "Resource logs in Batch accounts should be enabled",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Audit enabling of resource logs. This enables you to recreate activity trails to use for investigation purposes; when a security incident occurs or when your network is compromised",
        "metadata": {
          "version": "5.0.0",
          "category": "Batch"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          },
          "requiredRetentionDays": {
            "type": "String",
            "metadata": {
              "displayName": "Required retention (days)",
              "description": "The required resource logs retention in days"
            },
            "defaultValue": "365"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Batch/batchAccounts"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "existenceCondition": {
                "count": {
                  "field": "Microsoft.Insights/diagnosticSettings/logs[*]",
                  "where": {
                    "anyOf": [
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                            "equals": "true"
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days",
                                "equals": "0"
                              },
                              {
                                "value": "[padLeft(current('Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.days'), 3, '0')]",
                                "greaterOrEquals": "[padLeft(parameters('requiredRetentionDays'), 3, '0')]"
                              }
                            ]
                          },
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                            "equals": "true"
                          }
                        ]
                      },
                      {
                        "allOf": [
                          {
                            "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                            "equals": "true"
                          },
                          {
                            "anyOf": [
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/logs[*].retentionPolicy.enabled",
                                "notEquals": "true"
                              },
                              {
                                "field": "Microsoft.Insights/diagnosticSettings/storageAccountId",
                                "exists": false
                              }
                            ]
                          }
                        ]
                      }
                    ]
                  }
                },
                "greaterOrEquals": 1
              }
            }
          }
        }
      },
