              "existenceCondition": {
                "field": "Microsoft.Security/pricings/pricingTier",
                "equals": "Standard"
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4da35fc9-c9e7-4960-aec9-797fe7d9051d",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4da35fc9-c9e7-4960-aec9-797fe7d9051d"
    },
    {
      "properties": {
        "displayName": "Deploy Diagnostic Settings for Data Lake Analytics to Event Hub",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploys the diagnostic settings for Data Lake Analytics to stream to a regional Event Hub when any Data Lake Analytics which is missing this diagnostic settings is created or updated.",
        "metadata": {
          "version": "2.0.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "profileName": {
            "type": "String",
            "metadata": {
              "displayName": "Profile name",
              "description": "The diagnostic settings profile name"
            },
            "defaultValue": "setbypolicy_eventHub"
          },
          "eventHubRuleId": {
            "type": "String",
            "metadata": {
              "displayName": "Event Hub Authorization Rule Id",
              "description": "The Event Hub authorization rule Id for Azure Diagnostics. The authorization rule needs to be at Event Hub namespace level. e.g. /subscriptions/{subscription Id}/resourceGroups/{resource group}/providers/Microsoft.EventHub/namespaces/{Event Hub namespace}/authorizationrules/{authorization rule}",
              "strongType": "Microsoft.EventHub/Namespaces/AuthorizationRules",
              "assignPermissions": true
            }
          },
          "eventHubLocation": {
            "type": "String",
            "metadata": {
              "displayName": "Event Hub Location",
              "description": "The location the Event Hub resides in. Only Data Lake Analytics in this location will be linked to this Event Hub.",
              "strongType": "location"
            },
            "defaultValue": ""
          },
          "metricsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable metrics",
              "description": "Whether to enable metrics stream to the Event Hub - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "False"
          },
          "logsEnabled": {
            "type": "String",
            "metadata": {
              "displayName": "Enable logs",
              "description": "Whether to enable logs stream to the Event Hub  - True or False"
            },
            "allowedValues": [
              "True",
              "False"
            ],
            "defaultValue": "True"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.DataLakeAnalytics/accounts"
              },
              {
                "anyOf": [
                  {
                    "value": "[parameters('eventHubLocation')]",
                    "equals": ""
                  },
                  {
                    "field": "location",
                    "equals": "[parameters('eventHubLocation')]"
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Insights/diagnosticSettings",
              "name": "[parameters('profileName')]",
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/logs.enabled",
                    "equals": "[parameters('logsEnabled')]"
                  },
                  {
                    "field": "Microsoft.Insights/diagnosticSettings/metrics.enabled",
                    "equals": "[parameters('metricsEnabled')]"
                  }
                ]
              },
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "http://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "resourceName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      },
                      "eventHubRuleId": {
                        "type": "string"
                      },
                      "metricsEnabled": {
                        "type": "string"
                      },
                      "logsEnabled": {
                        "type": "string"
                      },
                      "profileName": {
                        "type": "string"
                      }
                    },
                    "variables": {},
                    "resources": [
                      {
                        "type": "Microsoft.DataLakeAnalytics/accounts/providers/diagnosticSettings",
                        "apiVersion": "2017-05-01-preview",
                        "name": "[concat(parameters('resourceName'), '/', 'Microsoft.Insights/', parameters('profileName'))]",
                        "location": "[parameters('location')]",
                        "dependsOn": [],
                        "properties": {
                          "eventHubAuthorizationRuleId": "[parameters('eventHubRuleId')]",
                          "metrics": [
                            {
                              "category": "AllMetrics",
                              "enabled": "[parameters('metricsEnabled')]",
                              "retentionPolicy": {
                                "enabled": false,
                                "days": 0
                              }
                            }
                          ],
                          "logs": [
                            {
                              "category": "Audit",
                              "enabled": "[parameters('logsEnabled')]"
                            },
                            {
                              "category": "Requests",
                              "enabled": "[parameters('logsEnabled')]"
                            }
                          ]
                        }
                      }
                    ],
                    "outputs": {}
                  },
                  "parameters": {
                    "location": {
                      "value": "[field('location')]"
                    },
                    "resourceName": {
                      "value": "[field('name')]"
                    },
                    "eventHubRuleId": {
                      "value": "[parameters('eventHubRuleId')]"
                    },
                    "metricsEnabled": {
                      "value": "[parameters('metricsEnabled')]"
                    },
                    "logsEnabled": {
                      "value": "[parameters('logsEnabled')]"
                    },
                    "profileName": {
                      "value": "[parameters('profileName')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4daddf25-4823-43d4-88eb-2419eb6dcc08",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4daddf25-4823-43d4-88eb-2419eb6dcc08"
    },
    {
      "properties": {
        "displayName": "Configure Batch accounts to disable local authentication",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disable location authentication methods so that your Batch accounts require Azure Active Directory identities exclusively for authentication. Learn more at: https://aka.ms/batch/auth.",
        "metadata": {
          "version": "1.0.0",
          "category": "Batch"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Modify",
              "Disabled"
            ],
            "defaultValue": "Modify"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Batch/batchAccounts"
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Batch/batchAccounts/allowedAuthenticationModes",
                    "exists": "false"
                  },
                  {
                    "count": {
                      "field": "Microsoft.Batch/batchAccounts/allowedAuthenticationModes[*]",
                      "where": {
                        "not": {
                          "field": "Microsoft.Batch/batchAccounts/allowedAuthenticationModes[*]",
                          "equals": "AAD"
                        }
                      }
                    },
                    "greater": 0
                  }
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "conflictEffect": "audit",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/b24988ac-6180-42a0-ab88-20f7382dd24c"
              ],
              "operations": [
                {
                  "condition": "[greaterOrEquals(requestContext().apiVersion, '2021-06-01')]",
                  "operation": "addOrReplace",
                  "field": "Microsoft.Batch/batchAccounts/allowedAuthenticationModes",
                  "value": [
                    "AAD"
                  ]
                }
              ]
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4dbc2f5c-51cf-4e38-9179-c7028eed2274",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4dbc2f5c-51cf-4e38-9179-c7028eed2274"
    },
    {
      "properties": {
        "displayName": "Cloud Services (extended support) role instances should have system updates installed",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Secure your Cloud Services (extended support) role instances by ensuring the latest security and critical updates are installed on them.",
        "metadata": {
          "version": "1.0.0",
          "category": "Security Center"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "AuditIfNotExists",
              "Disabled"
            ],
            "defaultValue": "AuditIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "field": "type",
            "equals": "Microsoft.Compute/cloudServices"
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Security/assessments",
              "name": "bd20bd91-aaf1-7f14-b6e4-866de2f43146",
              "existenceCondition": {
                "field": "Microsoft.Security/assessments/status.code",
                "in": [
                  "NotApplicable",
                  "Healthy"
                ]
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4df26ba8-026d-45b0-9521-bffa44d741d2",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4df26ba8-026d-45b0-9521-bffa44d741d2"
    },
    {
      "properties": {
        "displayName": "Configure Azure Cognitive Search services to disable local authentication",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disable local authentication methods so that your Azure Cognitive Search services exclusively require Azure Active Directory identities for authentication. Learn more at: https://aka.ms/azure-cognitive-search/rbac.",
        "metadata": {
          "version": "1.0.0",
          "category": "Search"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Modify",
              "Disabled"
            ],
            "defaultValue": "Modify"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Search/searchServices"
              },
              {
                "field": "Microsoft.Search/searchServices/disableLocalAuth",
                "notEquals": true
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "conflictEffect": "audit",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/7ca78c08-252a-4471-8644-bb5ff32d4ba0"
              ],
              "operations": [
                {
                  "condition": "[greaterOrEquals(requestContext().apiVersion, '2021-04-01')]",
                  "operation": "addOrReplace",
                  "field": "Microsoft.Search/searchServices/disableLocalAuth",
                  "value": true
                }
              ]
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4eb216f2-9dba-4979-86e6-5d7e63ce3b75",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4eb216f2-9dba-4979-86e6-5d7e63ce3b75"
    },
    {
      "properties": {
        "displayName": "Deploy - Configure private DNS zones for private endpoints that connect to Batch accounts",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Private DNS records allow private connections to private endpoints. Private endpoint connections allow secure communication by enabling private connectivity to Batch accounts without a need for public IP addresses at the source or destination. For more information on private endpoints and DNS zones in Batch, see https://docs.microsoft.com/azure/batch/private-connectivity.",
        "metadata": {
          "version": "1.0.0",
          "category": "Batch"
        },
        "parameters": {
          "privateDnsZoneId": {
            "type": "String",
            "metadata": {
              "displayName": "Private DNS Zone",
              "strongType": "Microsoft.Network/privateDnsZones",
              "description": "The private DNS zone to deploy in a new private DNS zone group and link to the private endpoint"
            }
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/privateEndpoints"
              },
              {
                "count": {
                  "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*].groupIds[*]",
                  "where": {
                    "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*].groupIds[*]",
                    "equals": "batchAccount"
                  }
                },
                "greaterOrEquals": 1
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Network/privateEndpoints/privateDnsZoneGroups",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/4d97b98b-1d4f-4787-a291-c67834d212e7"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2019-04-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "privateDnsZoneId": {
                        "type": "string"
                      },
                      "privateEndpointName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('privateEndpointName'), '/deployedByPolicy')]",
                        "type": "Microsoft.Network/privateEndpoints/privateDnsZoneGroups",
                        "apiVersion": "2020-03-01",
                        "location": "[parameters('location')]",
                        "properties": {
                          "privateDnsZoneConfigs": [
                            {
                              "name": "batchAccount-privateDnsZone",
                              "properties": {
                                "privateDnsZoneId": "[parameters('privateDnsZoneId')]"
                              }
                            }
                          ]
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "privateDnsZoneId": {
                      "value": "[parameters('privateDnsZoneId')]"
                    },
                    "privateEndpointName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4ec38ebc-381f-45ee-81a4-acbc4be878f8",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4ec38ebc-381f-45ee-81a4-acbc4be878f8"
    },
    {
      "properties": {
        "displayName": "Azure data factories should be encrypted with a customer-managed key",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Use customer-managed keys to manage the encryption at rest of your Azure Data Factory. By default, customer data is encrypted with service-managed keys, but customer-managed keys are commonly required to meet regulatory compliance standards. Customer-managed keys enable the data to be encrypted with an Azure Key Vault key created and owned by you. You have full control and responsibility for the key lifecycle, including rotation and management. Learn more at https://aka.ms/adf-cmk.",
        "metadata": {
          "version": "1.0.1",
          "category": "Data Factory"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.DataFactory/factories"
              },
              {
                "field": "Microsoft.DataFactory/factories/encryption.vaultBaseUrl",
                "exists": false
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/4ec52d6d-beb7-40c4-9a9e-fe753254690e",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "4ec52d6d-beb7-40c4-9a9e-fe753254690e"
    },
    {
      "properties": {
        "displayName": "Configure Windows virtual machine scale sets to run Azure Monitor Agent",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Automate the deployment of Azure Monitor Agent extension on your Windows virtual machine scale sets for collecting telemetry data from the guest OS. This policy will install the extension if the OS and region are supported and system-assigned managed identity is enabled, and skip install otherwise. Learn more: https://aka.ms/AMAOverview.",
        "metadata": {
          "version": "1.0.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy."
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "listOfWindowsImageIdToInclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Additional Virtual Machine Images",
              "description": "List of virtual machine images that have supported Windows OS to add to scope. Example values: '/subscriptions/<subscriptionId>/resourceGroups/YourResourceGroup/providers/Microsoft.Compute/images/ContosoStdImage'"
            },
            "defaultValue": []
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachineScaleSets"
              },
              {
                "field": "location",
                "in": [
                  "australiacentral",
                  "australiaeast",
                  "australiasoutheast",
                  "brazilsouth",
                  "canadacentral",
                  "canadaeast",
                  "centralindia",
                  "centralus",
                  "eastasia",
                  "eastus2euap",
                  "eastus",
                  "eastus2",
                  "francecentral",
                  "germanywestcentral",
                  "japaneast",
                  "japanwest",
                  "jioindiawest",
                  "koreacentral",
                  "koreasouth",
                  "northcentralus",
                  "northeurope",
                  "norwayeast",
                  "southafricanorth",
                  "southcentralus",
                  "southeastasia",
                  "southindia",
                  "switzerlandnorth",
                  "uaenorth",
                  "uksouth",
                  "ukwest",
                  "westcentralus",
                  "westeurope",
                  "westindia",
                  "westus",
                  "westus2"
                ]
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/imageId",
                    "in": "[parameters('listOfWindowsImageIdToInclude')]"
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "MicrosoftWindowsServer"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "WindowsServer"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
