      "id": "/providers/Microsoft.Authorization/policyDefinitions/53503636-bcc9-4748-9663-5348217f160f",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "53503636-bcc9-4748-9663-5348217f160f"
    },
    {
      "properties": {
        "displayName": "Cosmos DB database accounts should have local authentication methods disabled",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disabling local authentication methods improves security by ensuring that Cosmos DB database accounts exclusively require Azure Active Directory identities for authentication. Learn more at: https://docs.microsoft.com/azure/cosmos-db/how-to-setup-rbac#disable-local-auth.",
        "metadata": {
          "version": "1.0.0",
          "category": "Cosmos DB"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.DocumentDB/databaseAccounts"
              },
              {
                "field": "Microsoft.DocumentDB/databaseAccounts/disableLocalAuth",
                "notEquals": true
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/5450f5bd-9c72-4390-a9c4-a7aba4edfdd2",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "5450f5bd-9c72-4390-a9c4-a7aba4edfdd2"
    },
    {
      "properties": {
        "displayName": "App Service apps should use a SKU that supports private link",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "With supported SKUs, Azure Private Link lets you connect your virtual network to Azure services without a public IP address at the source or destination. The Private Link platform handles the connectivity between the consumer and services over the Azure backbone network. By mapping private endpoints to apps, you can reduce data leakage risks. Learn more about private links at: https://aka.ms/private-link.",
        "metadata": {
          "version": "1.0.0",
          "category": "App Service"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Web/serverFarms"
              },
              {
                "field": "Microsoft.Web/serverFarms/sku.family",
                "notIn": [
                  "Pv2",
                  "Pv3",
                  "EP",
                  "P",
                  "I",
                  "Iv2"
                ]
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/546fe8d2-368d-4029-a418-6af48a7f61e5",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "546fe8d2-368d-4029-a418-6af48a7f61e5"
    },
    {
      "properties": {
        "displayName": "[Preview]: Azure Key Vault should disable public network access",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Disable public network access for your key vault so that it's not accessible over the public internet. This can reduce data leakage risks. Learn more at: https://aka.ms/akvprivatelink.",
        "metadata": {
          "version": "2.0.0-preview",
          "category": "Key Vault",
          "preview": true
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.KeyVault/vaults"
              },
              {
                "not": {
                  "field": "Microsoft.KeyVault/vaults/createMode",
                  "equals": "recover"
                }
              },
              {
                "field": "Microsoft.KeyVault/vaults/networkAcls.defaultAction",
                "notEquals": "Deny"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/55615ac9-af46-4a59-874e-391cc3dfb490",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "55615ac9-af46-4a59-874e-391cc3dfb490"
    },
    {
      "properties": {
        "displayName": "Configure Azure Arc Private Link Scopes to use private DNS zones",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Use private DNS zones to override the DNS resolution for a private endpoint. A private DNS zone links to your virtual network to resolve to Azure Arc Private Link Scopes. Learn more at: https://aka.ms/arc/privatelink.",
        "metadata": {
          "version": "1.0.0",
          "category": "Azure Arc"
        },
        "parameters": {
          "privateDnsZoneIDForGuestConfiguration": {
            "type": "String",
            "metadata": {
              "displayName": "Private DNS Zone ID for privatelink.guestconfiguration.azure.com",
              "description": "The private DNS zone ID used to resolve a private DNS Zone to the Guest Configuration service.",
              "strongType": "Microsoft.Network/privateDnsZones",
              "assignPermissions": true
            }
          },
          "privateDnsZoneIDForHybridResourceProvider": {
            "type": "String",
            "metadata": {
              "displayName": "Private DNS Zone ID for privatelink.his.arc.azure.com",
              "description": "The private DNS zone ID used to resolve a private DNS Zone to the Azure Arc metadata and identity service.",
              "strongType": "Microsoft.Network/privateDnsZones",
              "assignPermissions": true
            }
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/privateEndpoints"
              },
              {
                "count": {
                  "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*]",
                  "where": {
                    "allOf": [
                      {
                        "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*].privateLinkServiceId",
                        "contains": "Microsoft.HybridCompute/privateLinkScopes"
                      },
                      {
                        "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*].groupIds[*]",
                        "equals": "hybridcompute"
                      }
                    ]
                  }
                },
                "greaterOrEquals": 1
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Network/privateEndpoints/privateDnsZoneGroups",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/4d97b98b-1d4f-4787-a291-c67834d212e7"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2019-04-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "privateDnsZoneIDForGuestConfiguration": {
                        "type": "string"
                      },
                      "privateDnsZoneIDForHybridResourceProvider": {
                        "type": "string"
                      },
                      "privateEndpointName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('privateEndpointName'), '/deployedByPolicy')]",
                        "type": "Microsoft.Network/privateEndpoints/privateDnsZoneGroups",
                        "apiVersion": "2020-03-01",
                        "location": "[parameters('location')]",
                        "properties": {
                          "privateDnsZoneConfigs": [
                            {
                              "name": "privatelink-guestconfiguration-azure-com",
                              "properties": {
                                "privateDnsZoneId": "[parameters('privateDnsZoneIDForGuestConfiguration')]"
                              }
                            },
                            {
                              "name": "privatelink-his-arc-azure-com",
                              "properties": {
                                "privateDnsZoneId": "[parameters('privateDnsZoneIDForHybridResourceProvider')]"
                              }
                            }
                          ]
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "privateDnsZoneIDForGuestConfiguration": {
                      "value": "[parameters('privateDnsZoneIDForGuestConfiguration')]"
                    },
                    "privateDnsZoneIDForHybridResourceProvider": {
                      "value": "[parameters('privateDnsZoneIDForHybridResourceProvider')]"
                    },
                    "privateEndpointName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/55c4db33-97b0-437b-8469-c4f4498f5df9",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "55c4db33-97b0-437b-8469-c4f4498f5df9"
    },
    {
      "properties": {
        "displayName": "Web Application Firewall (WAF) should be enabled for Application Gateway",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Deploy Azure Web Application Firewall (WAF) in front of public facing web applications for additional inspection of incoming traffic. Web Application Firewall (WAF) provides centralized protection of your web applications from common exploits and vulnerabilities such as SQL injections, Cross-Site Scripting, local and remote file executions. You can also restrict access to your web applications by countries, IP address ranges, and other http(s) parameters via custom rules.",
        "metadata": {
          "version": "1.0.1",
          "category": "Network"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/applicationGateways"
              },
              {
                "field": "Microsoft.Network/applicationGateways/webApplicationFirewallConfiguration",
                "exists": "false"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/564feb30-bf6a-4854-b4bb-0d2d2d1e6c66",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "564feb30-bf6a-4854-b4bb-0d2d2d1e6c66"
    },
    {
      "properties": {
        "displayName": "Configure Linux virtual machine scale sets to run Azure Monitor Agent",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Automate the deployment of Azure Monitor Agent extension on your Linux virtual machine scale sets for collecting telemetry data from the guest OS. This policy will install the extension if the OS and region are supported and system-assigned managed identity is enabled, and skip install otherwise. Learn more: https://aka.ms/AMAOverview.",
        "metadata": {
          "version": "1.0.0",
          "category": "Monitoring"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy."
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          },
          "listOfLinuxImageIdToInclude": {
            "type": "Array",
            "metadata": {
              "displayName": "Additional Virtual Machine Images",
              "description": "List of virtual machine images that have supported Linux OS to add to scope. Example values: '/subscriptions/<subscriptionId>/resourceGroups/YourResourceGroup/providers/Microsoft.Compute/images/ContosoStdImage'"
            },
            "defaultValue": []
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Compute/virtualMachineScaleSets"
              },
              {
                "field": "location",
                "in": [
                  "australiacentral",
                  "australiaeast",
                  "australiasoutheast",
                  "brazilsouth",
                  "canadacentral",
                  "canadaeast",
                  "centralindia",
                  "centralus",
                  "eastasia",
                  "eastus2euap",
                  "eastus",
                  "eastus2",
                  "francecentral",
                  "germanywestcentral",
                  "japaneast",
                  "japanwest",
                  "jioindiawest",
                  "koreacentral",
                  "koreasouth",
                  "northcentralus",
                  "northeurope",
                  "norwayeast",
                  "southafricanorth",
                  "southcentralus",
                  "southeastasia",
                  "southindia",
                  "switzerlandnorth",
                  "uaenorth",
                  "uksouth",
                  "ukwest",
                  "westcentralus",
                  "westeurope",
                  "westindia",
                  "westus",
                  "westus2"
                ]
              },
              {
                "anyOf": [
                  {
                    "field": "Microsoft.Compute/imageId",
                    "in": "[parameters('listOfLinuxImageIdToInclude')]"
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "RedHat"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "RHEL",
                          "RHEL-SAP-HANA"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "6*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "7*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "8*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "SUSE"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "SLES",
                          "SLES-HPC",
                          "SLES-HPC-Priority",
                          "SLES-SAP",
                          "SLES-SAP-BYOS",
                          "SLES-Priority",
                          "SLES-BYOS",
                          "SLES-SAPCAL",
                          "SLES-Standard"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "12*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "15*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "UbuntuServer"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "14.04*LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "16.04*LTS"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "18.04*LTS"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Canonical"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "0001-com-ubuntu-server-focal"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "20_04-lts*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Oracle"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "Oracle-Linux"
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "6*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "7*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "8*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "OpenLogic"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "CentOS",
                          "Centos-LVM",
                          "CentOS-SRIOV"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "6*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "7*"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "8*"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "cloudera"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "equals": "cloudera-centos-os"
                      },
                      {
                        "field": "Microsoft.Compute/imageSku",
                        "like": "7*"
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "credativ"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "debian"
                        ]
                      },
                      {
                        "anyOf": [
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "8"
                          },
                          {
                            "field": "Microsoft.Compute/imageSku",
                            "like": "9"
                          }
                        ]
                      }
                    ]
                  },
                  {
                    "allOf": [
                      {
                        "field": "Microsoft.Compute/imagePublisher",
                        "equals": "Debian"
                      },
                      {
                        "field": "Microsoft.Compute/imageOffer",
                        "in": [
                          "debian-10"
                        ]
                      },
                      {
                        "field": "Microsoft.Compute/imageSku",
                        "like": "10"
                      }
                    ]
                  }
                ]
              },
              {
                "field": "identity.type",
                "contains": "SystemAssigned"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Compute/virtualMachineScaleSets/extensions",
              "roleDefinitionIds": [
                "/providers/microsoft.authorization/roleDefinitions/9980e02c-c2be-4d73-94e8-173b1dc7cf3c"
              ],
              "existenceCondition": {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/type",
                    "equals": "AzureMonitorLinuxAgent"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/publisher",
                    "equals": "Microsoft.Azure.Monitor"
                  },
                  {
                    "field": "Microsoft.Compute/virtualMachineScaleSets/extensions/provisioningState",
                    "equals": "Succeeded"
                  }
                ]
              },
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2015-01-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "vmName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "variables": {
                      "extensionName": "AzureMonitorLinuxAgent",
                      "extensionPublisher": "Microsoft.Azure.Monitor",
                      "extensionType": "AzureMonitorLinuxAgent",
                      "extensionTypeHandlerVersion": "1.12"
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('vmName'), '/', variables('extensionName'))]",
                        "type": "Microsoft.Compute/virtualMachineScaleSets/extensions",
                        "location": "[parameters('location')]",
                        "apiVersion": "2019-07-01",
                        "properties": {
                          "publisher": "[variables('extensionPublisher')]",
                          "type": "[variables('extensionType')]",
                          "typeHandlerVersion": "[variables('extensionTypeHandlerVersion')]",
                          "autoUpgradeMinorVersion": true
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "vmName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/56a3e4f8-649b-4fac-887e-5564d11e8d3a",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "56a3e4f8-649b-4fac-887e-5564d11e8d3a"
    },
    {
      "properties": {
        "displayName": "Azure Automation accounts should use customer-managed keys to encrypt data at rest",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Use customer-managed keys to manage the encryption at rest of your Azure Automation Accounts. By default, customer data is encrypted with service-managed keys, but customer-managed keys are commonly required to meet regulatory compliance standards. Customer-managed keys enable the data to be encrypted with an Azure Key Vault key created and owned by you. You have full control and responsibility for the key lifecycle, including rotation and management. Learn more at https://aka.ms/automation-cmk.",
        "metadata": {
          "version": "1.0.0",
          "category": "Automation"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "'Audit' allows a non-compliant resource to be created or updated, but flags it as non-compliant. 'Deny' blocks the non-compliant resource creation or update. 'Disabled' turns off the policy."
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Automation/automationAccounts"
              },
              {
                "field": "Microsoft.Automation/automationAccounts/encryption.keySource",
                "notEquals": "Microsoft.Keyvault"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
      "id": "/providers/Microsoft.Authorization/policyDefinitions/56a5ee18-2ae6-4810-86f7-18e39ce5629b",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "56a5ee18-2ae6-4810-86f7-18e39ce5629b"
    },
    {
      "properties": {
        "displayName": "IP firewall rules on Azure Synapse workspaces should be removed",
        "policyType": "BuiltIn",
        "mode": "All",
        "description": "Removing all IP firewall rules improves security by ensuring your Azure Synapse workspace can only be accessed from a private endpoint. This configuration audits creation of firewall rules that allow public network access on the workspace.",
        "metadata": {
          "version": "1.0.0",
          "category": "Synapse"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Synapse/workspaces/firewallrules"
              },
              {
                "field": "name",
                "exists": "true"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
