          "category": "SQL"
        },
        "parameters": {
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.DBforPostgreSQL/flexibleServers"
              },
              {
                "field": "Microsoft.DBforPostgreSQL/flexibleServers/publicNetworkAccess",
                "notEquals": "Disabled"
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]"
          }
        }
      },
