            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.ContainerService/managedClusters",
              "deploymentScope": "subscription",
              "existenceCondition": {
                "field": "Microsoft.ContainerService/managedClusters/securityProfile.azureDefender.enabled",
                "equals": "true"
