    {
      "properties": {
        "displayName": "Configure BotService resources to use private DNS zones",
        "policyType": "BuiltIn",
        "mode": "Indexed",
        "description": "Use private DNS zones to override the DNS resolution for a private endpoint. A private DNS zone links to your virtual network to resolve to BotService related resources. Learn more at: https://aka.ms/privatednszone.",
        "metadata": {
          "version": "1.0.0",
          "category": "Bot Service"
        },
        "parameters": {
          "privateDnsZoneId": {
            "type": "String",
            "metadata": {
              "displayName": "Private Dns Zone Id",
              "description": "The private DNS zone to deploy in a new private DNS zone group and link to the private endpoint",
              "strongType": "Microsoft.Network/privateDnsZones"
            }
          },
          "privateEndpointGroupId": {
            "type": "String",
            "metadata": {
              "displayName": "Private Endpoint Group Id",
              "description": "A group Id for the private endpoint for a BotService resource."
            },
            "allowedValues": [
              "Bot",
              "Token"
            ],
            "defaultValue": "Bot"
          },
          "effect": {
            "type": "String",
            "metadata": {
              "displayName": "Effect",
              "description": "Enable or disable the execution of the policy"
            },
            "allowedValues": [
              "DeployIfNotExists",
              "Disabled"
            ],
            "defaultValue": "DeployIfNotExists"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Network/privateEndpoints"
              },
              {
                "count": {
                  "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*]",
                  "where": {
                    "allOf": [
                      {
                        "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*].privateLinkServiceId",
                        "contains": "Microsoft.BotService/botServices"
                      },
                      {
                        "field": "Microsoft.Network/privateEndpoints/privateLinkServiceConnections[*].groupIds[*]",
                        "equals": "[parameters('privateEndpointGroupId')]"
                      }
                    ]
                  }
                },
                "greaterOrEquals": 1
              }
            ]
          },
          "then": {
            "effect": "[parameters('effect')]",
            "details": {
              "type": "Microsoft.Network/privateEndpoints/privateDnsZoneGroups",
              "roleDefinitionIds": [
                "/providers/Microsoft.Authorization/roleDefinitions/4d97b98b-1d4f-4787-a291-c67834d212e7"
              ],
              "deployment": {
                "properties": {
                  "mode": "incremental",
                  "template": {
                    "$schema": "https://schema.management.azure.com/schemas/2019-04-01/deploymentTemplate.json#",
                    "contentVersion": "1.0.0.0",
                    "parameters": {
                      "privateDnsZoneId": {
                        "type": "string"
                      },
                      "privateEndpointName": {
                        "type": "string"
                      },
                      "location": {
                        "type": "string"
                      }
                    },
                    "resources": [
                      {
                        "name": "[concat(parameters('privateEndpointName'), '/deployedByPolicy')]",
                        "type": "Microsoft.Network/privateEndpoints/privateDnsZoneGroups",
                        "apiVersion": "2020-03-01",
                        "location": "[parameters('location')]",
                        "properties": {
                          "privateDnsZoneConfigs": [
                            {
                              "name": "BotService-privateDnsZone",
                              "properties": {
                                "privateDnsZoneId": "[parameters('privateDnsZoneId')]"
                              }
                            }
                          ]
                        }
                      }
                    ]
                  },
                  "parameters": {
                    "privateDnsZoneId": {
                      "value": "[parameters('privateDnsZoneId')]"
                    },
                    "privateEndpointName": {
                      "value": "[field('name')]"
                    },
                    "location": {
                      "value": "[field('location')]"
                    }
                  }
                }
              }
            }
          }
        }
      },
