      "id": "/providers/Microsoft.Authorization/policyDefinitions/7590a335-57cf-4c95-babd-ecbc8fafeb1f",
      "type": "Microsoft.Authorization/policyDefinitions",
      "name": "7590a335-57cf-4c95-babd-ecbc8fafeb1f"
    },
    {
      "properties": {
