            "allowedValues": [
              "Audit",
              "Deny",
              "Disabled"
            ],
            "defaultValue": "Audit"
          }
        },
        "policyRule": {
          "if": {
            "allOf": [
              {
                "field": "type",
                "equals": "Microsoft.Search/searchServices"
              },
              {
                "anyof": [
                  {
                    "field": "Microsoft.Search/searchServices/encryptionWithCmk.enforcement",
                    "notEquals": "Enabled"
                  },
                  {
                    "field": "Microsoft.Search/searchServices/encryptionWithCmk.encryptionComplianceStatus",
                    "notEquals": "Compliant"
