    {
      "id": "/providers/Microsoft.Security/assessmentMetadata/22438e3c-73c8-40af-a083-10c980c63aa2",
      "name": "22438e3c-73c8-40af-a083-10c980c63aa2",
      "type": "Microsoft.Security/assessmentMetadata",
      "properties": {
        "displayName": "Ensure CloudTrail is enabled in all regions",
        "assessmentType": "BuiltIn",
        "policyDefinitionId": "",
        "description": "CloudTrail is a service that records AWS API calls for your account and delivers log files to you. The recorded information includes the identity of the API caller, the time of the API call, the source IP address of the API caller, the request parameters, and the response elements returned by the AWS service. CloudTrail provides a history of AWS API calls for an account, including API calls made via the AWS Management Console, AWS SDKs, command-line tools, and higher-level AWS services (such as AWS CloudFormation). The AWS API call history produced by CloudTrail enables security analysis, resource change tracking, and compliance auditing. Additionally: Ensuring that a multi-Region trail exists ensures that unexpected activity occurring in otherwise unused Regions is detected; Ensuring that a multi-Region trail exists ensures that Global Service Logging is enabled for a trail by default to capture recording of events generated on AWS global services; For a multi-Region trail, ensuring that management events configured for all type of Read/Writes ensures recording of management operations that are performed on all resources in an AWS account.",
        "remediationDescription": "For instructions on how to remediate this issue, please visit the <a target=\"_blank\" href=\"https://docs.aws.amazon.com/console/securityhub/standards-cis-2.1/remediation\">AWS Security Hub CIS documentation</a>",
        "categories": [
          "IdentityAndAccess"
        ],
        "preview": true,
        "severity": "High",
        "userImpact": "Low",
        "implementationEffort": "Low",
        "threats": [
          "AccountBreach",
          "DataExfiltration",
          "DataSpillage",
          "MaliciousInsider"
        ],
        "publishDates": {
          "public": "06/01/2021"
        },
        "cloudProviders": [
