    {
      "id": "/providers/Microsoft.Security/assessmentMetadata/0384d5b7-5def-4130-b7b5-db7da7e63276",
      "name": "0384d5b7-5def-4130-b7b5-db7da7e63276",
      "type": "Microsoft.Security/assessmentMetadata",
      "properties": {
        "displayName": "Ensure the S3 bucket CloudTrail logs to is not publicly accessible",
        "assessmentType": "BuiltIn",
        "policyDefinitionId": "",
        "description": "CloudTrail logs a record of every API call made in your account. These log files are stored in an S3 bucket. Security Hub recommends that the S3 bucket policy, or access control list (ACL), applied to the S3 bucket that CloudTrail logs to prevents public access to the CloudTrail logs. Allowing public access to CloudTrail log content might aid an adversary in identifying weaknesses in the affected account's use or configuration. To run this check, Security Hub first uses custom logic to look for the S3 bucket where your CloudTrail logs are stored. It then uses the AWS Config managed rules to check that bucket is publicly accessible. If you aggregate your logs into a single centralized S3 bucket, then Security Hub only runs the check against the account and Region where the centralized S3 bucket is located. For other accounts and Regions, the control status is 'No data'. If the bucket is publicly accessible, the check generates a failed finding.",
        "remediationDescription": "For instructions on how to remediate this issue, please visit the <a target=\"_blank\" href=\"https://docs.aws.amazon.com/console/securityhub/standards-cis-2.3/remediation\">AWS Security Hub CIS documentation</a>",
        "categories": [
          "Data"
        ],
        "preview": true,
        "severity": "High",
        "userImpact": "Low",
        "implementationEffort": "Low",
        "threats": [
          "AccountBreach",
          "DataExfiltration",
          "DataSpillage",
          "MaliciousInsider"
        ],
        "publishDates": {
