    {
      "id": "/providers/Microsoft.Security/assessmentMetadata/a9ac48d8-8dd7-42b9-9752-b1fa70ea5dd9",
      "name": "a9ac48d8-8dd7-42b9-9752-b1fa70ea5dd9",
      "type": "Microsoft.Security/assessmentMetadata",
      "properties": {
        "displayName": "Ensure VPC flow logging is enabled in all VPCs",
        "assessmentType": "BuiltIn",
        "policyDefinitionId": "",
        "description": "VPC flow logs is a feature that enables you to capture information about the IP traffic going to and from network interfaces in your VPC. After you have created a flow log, you can view and retrieve its data in CloudWatch Logs. Security Hub recommends that you enable flow logging for packet rejects for VPCs. Flow logs provide visibility into network traffic that traverses the VPC and can detect anomalous traffic or insight during security workflows.",
        "remediationDescription": "For instructions on how to remediate this issue, please visit the <a target=\"_blank\" href=\"https://docs.aws.amazon.com/console/securityhub/standards-cis-2.9/remediation\">AWS Security Hub CIS documentation</a>",
