    {
      "id": "/providers/Microsoft.Security/assessmentMetadata/a269cbdb-86e8-431c-9ff2-f0ea491174d8",
      "name": "a269cbdb-86e8-431c-9ff2-f0ea491174d8",
      "type": "Microsoft.Security/assessmentMetadata",
      "properties": {
        "displayName": "Ensure a log metric filter and alarm exist for usage of \"root\" account",
        "assessmentType": "BuiltIn",
