    {
      "id": "/providers/Microsoft.Security/assessmentMetadata/91af2040-7874-4659-abf0-578e1f8d07dc",
      "name": "91af2040-7874-4659-abf0-578e1f8d07dc",
      "type": "Microsoft.Security/assessmentMetadata",
      "properties": {
        "displayName": "API endpoints in Azure API Management should be authenticated",
        "assessmentType": "BuiltIn",
        "policyDefinitionId": "",
        "description": "Authentication mechanisms are often implemented incorrectly or are missing. This allows attackers to exploit implementation flaws and to access data. API endpoints published within Azure API Management should enforce authentication to help minimize this risk. <a href=\"https://learn.microsoft.com/en-us/azure/api-management/mitigate-owasp-api-threats#broken-user-authentication\" target=\"_blank\">Learn More</a>",
