        },
        "additionalData": {
          "assessedResourceType": "SqlServerVulnerability",
          "type": "AzureDatabase",
          "query": "SELECT user_name(roles.role_principal_id) AS [role]\n    ,user_name(roles.member_principal_id) AS member\nFROM sys.database_role_members AS roles\nINNER JOIN sys.database_principals AS users\nON roles.member_principal_id = users.principal_id\n    AND (\n        roles.role_principal_id >= 16384\n        AND roles.role_principal_id <= 16393\n        )\n    AND users.type = 'R'\nORDER BY user_name(roles.role_principal_id)\n    ,user_name(roles.member_principal_id)",
          "benchmarks": [
