      "name": "73e2b780-d295-07b5-aae6-77dd7a33545f",
      "properties": {
        "id": "VA2108",
        "displayName": "Minimal set of principals should be members of fixed high impact database roles",
        "status": {
          "code": "Healthy",
          "severity": "High",
          "cause": "Unknown"
        },
        "remediation": "Remove members who should not have access to the database role",
        "impact": "Fixed database roles may have administrative permissions on the system. Following the principle of least privilege, it is important to minimize membership in fixed database roles and keep a baseline of these memberships. See https://docs.microsoft.com/en-us/sql/relational-databases/security/authentication-access/database-level-roles for additional information on database roles.",
        "category": "AuthenticationAndAuthorization",
        "description": "SQL Server provides roles to help manage the permissions. Roles are security principals that group other principals. Database-level roles are database-wide in their permission scope. This rule checks that a minimal set of principals are members of the fixed database roles.",
        "timeGenerated": "2020-11-26T16:48:59.4578972Z",
        "resourceDetails": {
          "source": "Azure",
          "id": "/subscriptions/487bb485-b5b0-471e-9c0d-10717612f869/resourceGroups/myService1/providers/Microsoft.Sql/servers/datastore/databases/dataStore"
        },
        "additionalData": {
          "assessedResourceType": "SqlServerVulnerability",
          "type": "AzureDatabase",
          "query": "SELECT user_name(sr.member_principal_id) as [Principal]\n    ,user_name(sr.role_principal_id) as [Role]\n    ,type_desc as [Principal Type]\n    ,authentication_type_desc as [Authentication Type]\nFROM sys.database_role_members AS sr \nINNER JOIN sys.database_principals AS sp ON sp.principal_id = sr.member_principal_id\nWHERE sr.role_principal_id IN (user_id('bulkadmin'),\n                             user_id('db_accessadmin'),\n                             user_id('db_securityadmin'),\n                             user_id('db_ddladmin'),\n                             user_id('db_backupoperator'))\nORDER BY sp.name",
          "benchmarks": [
            {
              "benchmark": "FedRAMP"
            }
          ]
        }
      }
