command:example.dev:code:
PS C:\> $s = New-AzureDiskConfigSet
          # returns DiskConfigSet object;

          $s = Set-AzureOSDiskConfig -DiskConfig $s -HostCaching ReadWrite
          # returns DiskConfigSet object;

          $s = Set-AzureDataDiskConfig -DiskConfig $s -Name 'test' -HostCaching 'ReadWrite' -LUN 0
          # returns DiskConfigSet object

          # Final step - Update the VM Image

          Update-AzureVMImage -ImageName testvmimage2 -Label test1 -Description test1 -DiskConfigSet $s;

          # OR

          $s | Update-AzureVMImage -ImageName testvmimage2 -Label test1 -Description test1;

PS C:\> $subsId = &amp;lt;MySubscriptionID&amp;gt;
$cert = Get-AzureCertificate cert:\LocalMachine\MY\&amp;lt;CertificateThumbprint&amp;gt;
Get-AzureVMImage `
    | Where-Object {$_.Label -match "Beta" }`
    | Foreach-Object {Remove-AzureVMImage -ImageName $_.name }

PS C:\> $s = New-AzureDiskConfigSet
          # returns DiskConfigSet object;

          $s = Set-AzureDataDiskConfig -DiskConfig $s -HostCaching ReadWrite
          # returns DiskConfigSet object;

          Remove-AzureVMImageDataDiskConfig -DiskConfig $s
          # removes the Data DiskConfig object from the DiskConfigSet object

PS C:\> $s = New-AzureDiskConfigSet
          # returns DiskConfigSet object;

          $s = Set-AzureOSDiskConfig -DiskConfig $s -HostCaching ReadWrite
          # returns DiskConfigSet object;

          Remove-AzureVMImageOSDiskConfig -DiskConfig $s
          # removes the OS DiskConfig object from the DiskConfigSet object

PS C:\> # Prerequisite: VNet has been set up with SubNet
          # Set-AzureVNetConfig -ConfigurationPath $vnetConfigPath;

          $vm = New-AzureVMConfig -Name $vmname -ImageName $img -InstanceSize $sz | Add-AzureProvisioningConfig -Windows -Password $pwd -AdminUsername $usr;
          $vm = Set-AzureSubNet -VM $vm -SubNetNames $sn;
          Set-AzureStaticVNETIP -IPAddress $ip -VM $vm;
          New-AzureVM -ServiceName $svc -VMs $vm -VNETName $vnetName -Location $loc;

